INSERT INTO `fr_evaluation` (`id`, `content`, `focus`, `name`, `page`, `type`, `value`, `year`, `is_special`) VALUES
	(1, '依据班子成员“一岗双责”及其下位责任实际，按要求参加及召开“1+X”会议，分析分管、联系及负责领域、分管基层站所等全面从严治党形势任务、问题风险，以及“七张问题清单”状况，明晰各方职责任务，研究解决履责突出问题，推进各方职责任务调整、完善及有效落实。', '按规定次数（全年4次）、规定时间（每季度次月10日前）参加并提交审核通过职责任务、工作进展等。每少参加1次扣0.5分，未提交审核通过职责任务、工作进展的，每次扣0.5分，每已超过规定时间（即每季度次月15日前）提交审核通过职责任务、工作进展的1次扣0.2分，以上扣分按80%权重折算。分管领域、站所按其扣分情况按20%权重折算（分管多个站所的按比例分派，下同）。', '“1+X”专题会议', 1, '明责评价', 4.00, 2022, NULL),
	(2, '	班子成员“一岗双责”及其下位责任主体，坚持问题、风险导向，特别是依据全面从严治党、“七张问题清单”导向，查找、建立分管、联系及负责领域、分管基层站所各主体等“问题清单”并全程跟进调整完善，同时有针对性地建立“措施清单”及依据上级移交、反馈、交办的“问题清单”有效、动态建立“措施清单”，确保明确履责方向、突出履责重点、强化履责协同、有效落实责任清单。', '按规定条数（4条及以上）、规定时间（3月31日前，2022年为8月31日前）建立责任清单，每少1条扣2分（其中每少1条措施清单扣1分），以上扣分按80%权重折算。分管领域、站所按其扣分情况按20%权重折算。', '建立责任清单', 1, '明责评价', 8.00, 2022, NULL),
	(3, '班子成员按要求参加区（镇）党委“第一议题”（含学规学纪）学习，指导督促分管、联系及负责领域、分管基层站所认真制定年度安排计划，坚持每月不少于1次组织开展学习、宣传、教育、警示等活动，推动班子成员及分管主体做到“两个维护”，提高政治站位，强化纪律规矩意识，守牢法纪底线红线。', '	按规定次数（以系统提交为准）参加，每少1次扣0.2分，以上扣分按80%权重折算。分管领域、站所按其扣分情况按20%权重折算。', '“第一议题”制度', 1, '明责评价', 2.00, 2022, NULL),
	(4, '班子成员“一岗双责”及其下位责任主体，依据自我查找、建立的，以及上级移交、反馈、交办的分管、联系及负责领域、分管基层站所各主体等“问题清单”及相应建立的“措施清单”等，全程动态推进整改进展直至整改完成销号，形成落实“整改清单”，实现各责任主体能动、协同履责工作到位，提升管党治党责任落实成效。', ' 按规定时间（2022年12月31日前）完成问题清单整改销号，未完成整改销号1条扣2分，每已超出规定时间（即2023年1月10日前）完成整改销号1条扣1分，以上扣分按80%权重折算。分管领域、分管站所按其扣分情况按20%权重折算。', '落实责任清单', 1, '履责评价', 16.00, 2022, NULL),
	(5, '	根据“5+1”谈话机制要求，班子成员认真落实对分管、联系及负责领域、分管基层站所“一把手”开展日常谈话、受委托开展任职谈话及监督谈话，讲明职责任务，对苗头性、倾向性谈话及时进行提醒。认真落实区（镇）“一把手”对班子成员日常谈话和监督谈话要求，抓实“一岗双责”，注重自身问题、风险防范处置。', '  按规定时间（2022年12月31日前）完成分管“一把手”日常谈话、受委托开展任职谈话或监督谈话，未完成谈话的每人次扣1分，扣完为止。', '	“5+1”谈话机制', 1, '履责评价', 2.00, 2022, NULL),
	(6, '	班子成员“一岗双责”及其下位责任主体，围绕上级重大决策部署及分管、联系及负责领域、分管基层站所党风廉政建设重点领域，“七张问题清单”所涉内容等，相关主体每季度不少于1次组织（联合）开展监督检查，加强问题、风险排查发现及有效处置、整改。', ' 按规定次数（全年4次、半年2次）、规定时间（每季度次月10日前，最迟2023年1月10日前）完成监督检查并提交，每少1次扣1分（2022年设为2次）扣完为止。全年累计发现并完成办理（交办）问题不少于2个（2022年为1个），每少1个扣1分，以上扣分按80%权重折算。分管领域、站所按其扣分情况按20%权重折算。', '监督检查工作', 1, '履责评价', 4.00, 2022, NULL),
	(7, '班子成员及其下位责任主体，严格按照区(镇）“三重一大”事项目录清单和议事规则规定要求，履行分管、联系领域及负责领域、分管基层站所涉“三重一大”事项决策必要前置程序，执行落实议事决策全流程纪实，分管领域自觉接受集体领导和有效监督，抓好相关问题整改。主动参与区（镇）“三重一大”事项决策监督，充分发表意见，特别是加强对“一把手”违反民主集中制问题的监督。', ' 拟提交党委会议决策的“三重一大”事项经审核被退回的，每个事项扣1分；提交党委会议决策的“三重一大”事项经决策，决策结果为退回重新拟定方案、意见等再行决策或终止提交方案、意见等不再执行的，每个事项扣1分；决策过程中出现班子成员未充分发表意见或意见分歧较大但强行决策通过的或未执行末位表态情形的，“一把手”每次扣1分；通过决策事项被认定为执行发生偏差、缓执行、不执行情形的，每个事项扣1分，扣完为止。', '“三重一大”决策', 1, '履责评价', 10.00, 2022, NULL),
	(8, '班子成员“一岗双责”及其下位责任主体主动抓好监督执纪“四种形态”，主动适用第一种形态处置问题，主动发现、报告所分管、负责、联系领域、基层站所干部和公职人员的违纪违规违法问题。', '  每年（12月31日前）不开展分管领域、站所开展第一种形态处置的扣2分，分管领域、站所同时发生被区（镇）党委、纪委及以上采取“四种形态”处置问题的扣2分，扣完为止；每年对分管经发、村建、综合执法仅开展1人次第一种形态处置的扣1分，对分管其他站所达到1次不扣分；多名班子成员分管同一站所的第一种形态处置数量合并计算，一名班子成员分管多个站办的第一种形态处置分开计算，以上扣分按80%权重折算。分管领域、站所按其扣分情况按20%权重折算。	', '第一种形态运用）', 1, '履责评价', 4.00, 2022, NULL),
	(9, '班子成员发现同级班子其他成员，特别是“一把手”存在的苗头性、倾向性问题及时进行相互监提醒，对发现存在的重要问题，及时向区（镇）“一把手”或上级如实报告。同时，主动接受其他班子成员对本人的监督提醒，切实抓好问题整改。', '每年（12月31日前）规定时间内至少对其他班子成员开展1次相互监督提醒，未开展扣2分。', '相互监督提醒', 1, '履责评价', 2.00, 2022, NULL),
	(10, '班子成员对照“一岗双责”责任清单、协同履责绩效考评项目等，客观、如实、及时向上级报告履责情况，特别是主动做好向区（镇）“一把手”民主生活会前个别汇报、党委专题会议汇报及特殊情况随时汇报等，根据监督评议意见、“一把手”约谈意见及纪委情况通报等，抓好突出问题及风险整改。同时，认真抓好下位责任主体履责报告等职责范围内的监督评议工作，督促跟进评议反馈问题，特别是纪委情况通报问题的整改落实。', '每年不按规定在党委专题会议上、民主生活会前向“一把手”履责报告的，每次扣1分；遇特殊情况未做好随时报告的，扣2分；未按监督评议意见抓好整改的扣2分。对分管领域、站所及负责人履责报告未作出监督评议意见的，每次扣1分，以上扣分按80%权重折算。分管领域、站所按其扣分情况按20%权重折算。', '履责情况报告）', 1, '履责评价', 4.00, 2022, NULL),
	(11, '班子成员在每年年底区（镇）党委（扩大）会议上述责述廉，把包括执行政治纪律和政治规矩、贯彻落实中央决策部署、履行管党治党责任、担当作为、工作实绩、履职用权、廉洁自律等方面，并接受评议。', '每年未在党委会议上述责述廉的，扣2分；评议结果为基本称职、不定等次的扣1分、不称职的扣2分。', '述责述廉评议', 1, '履责评价', 4.00, 2022, NULL),
	(12, '班子成员认真落实个人有关事项报告规定，如实、及时做好即时报告、年度报告工作，每年民主生活进行书面公开，新任职“一把手”按规定做好到任后第一次党委会上进行口头公开。	', '不按规定报告个人事项的每次扣1分；每年民主生活会未进行书面公开的，每次扣1分；“一把手”未按规定做好新到任口头公开的，扣1分。', '个人有关事项报告及公开', 1, '履责评价', 2.00, 2022, b'1'),
	(13, '班子成员按照区（镇）领导班子民主生活会要求，聚焦主题，认真开展理论学习、征求意见建议、相互谈心谈话、高质量撰写发言提纲、严肃开展批评和自我批评、主动做好相关问题情况说明、抓好问题整改落实。按照组织生活会督导要求，指导参加联系村（社）组织生活会，带领督导组抓好会前检查、会中指导、会后评估及跟进查摆问题整改工作。', '未认真撰写发言提纲、严肃开展批评与自我批评、主动做好相关问题情况说明、抓好整改问题落实的，每次扣1分；未认真指导参加联系村（社）组织生活会、抓好检查指导评估及督促落实整改的，每次扣1分。', '民主（组织）生活会（督导）', 1, '履责评价', 2.00, 2022, b'1'),
	(14, '班子成员要全面如实记录相关干部本人或授意他人插手干预干部选拔任用、土地使用权出让、工程建设、房地产开发与经营、执纪执法活动等重大事项情况并第一时间做好向区（镇）党委报告工作。', '发生未如实记录报告领导干部插手干预重大事项的，每次扣1分，扣完为止。', '领导干部插手干预重大事项记录报告', 1, '履责评价', 2.00, 2022, b'1'),
	(15, '班子成员按照落实“一岗双责”要求，认真抓好分管清廉机关（基层站所）建设，严格执行落实议事规则、集体决策机制及工程项目招投标制度规定等，严格执行行业、业务工作法律法规、政策制度规定，健全单位内部治理、内控、监督机制，全面规范权力运行，加强机关、站所）清廉文化建设，积极打造清廉单元建设示范标杆。', '分管领域、站所发生未严格执行落实议事规则、集体决策机制及工程项目招投标制度规定等、未严格执行行业、业务工作法律法规、政策制度规定，未健全单位内部治理、内控、监督机制，未抓好清廉文化建设等情形的，每次扣1分。', '清廉单元建设', 1, '履责评价', 4.00, 2022, b'1'),
	(16, '班子成员按照落实“一岗双责”要求，认真抓好“一单三书”制度的执行落实，对所涉的各类问题、风险，各主体强化责任落实，协同落实整改，层层落实管党治党责任。认真配合上级巡察、基层作风巡查、专项监督、专项治理等反馈问题的整改落实。', '未在规定时间内抓好抄告单、第一种形态告知书执行落实及反馈的，每次扣1分，以上扣分按80%权重折算；未督促分管领域、站所各主体在规定时间内抓好意见书、建议书、第一种形态告知书执行落实及反馈的，按其扣分情况按20%权重折算。', '“一单三书”', 1, '督责评价', 2.00, 2022, NULL),
	(17, '班子成员按照落实“一岗双责”要求，认真抓好“纪委动议”制度的执行落实，对所涉的各类问题、风险及其他工作职责任务，各主体协同落实抓好整改及部署落实，防控风险、解决问题。认真抓好上级纪委专项监督、纪委专项治理及纪检监察建议书、正风肃纪移送函等按要求办理落实。', '未在规定时间内抓好“纪委动议”事项执行落实及反馈的，每次扣1分，扣完为止。', '纪委动议', 1, '督责评价', 2.00, 2022, NULL),
	(18, '班子成员针对各责任主体日常监督等发现的问题，抓好“六必谈”等情形的履责约谈，同步抓好被上级履责约谈的整改落实，不断增强相关责任主体的履责意识、履责动力，推动各类问题、风险有效整改。', '未在年内（12月31日前）完成对履责约谈，每人次扣1分，以上扣分按80%权重折算；分管领域、站所按其扣分情况按20%权重折算。', '履责约谈', 1, '督责评价', 2.00, 2022, NULL),
	(19, '对班子成员“一岗双责”及其下位主体履责不到位触发相关预警、公权力大数据监督应用预警等发现涉班子成员及下位主体的问题风险等，落实班子成员追踪督促、跟进有效闭环整改。', '未在规定时间内完成对履责不到位模型触发的预警、公权力大数据监督应用触发预警跟踪处置及闭环整改的，每次扣2分，以上扣分按80%权重折算。分管领域、站所主体未完成预警跟踪处置及闭环整改的，按其扣分情况按20%权重折算。', '履责追踪', 1, '督责评价', 4.00, 2022, NULL),
	(20, '对班子成员“一岗双责”及其下位主体履责用权预警、公权力大数据监督应用预警涉班子成员及下位主体的问题风险，处置整改不力或拒不整改、整改搞形式主义、官僚主义造成不良影响或严重后果的，实施预警处置并追究责任。', '未积极处置、拒不处置、搞形式主义、官僚主义处置履责预警、公权力大数据监督预警，造成不良影响或后果的，扣3分，以上扣分按80%权重折算。分管领域、站所主体按其扣分情况按20%权重折算。', '预警处置', 1, '追责评价', 4.00, 2022, b'1'),
	(21, '发生违纪违规违法情况,或在推进上级党委、政府中心工作、重点工作中发生不作为慢作为乱作为假作为等问题的，班子成员及其下位主体干部职工被区（镇）及以上机关查处的一般给予相应扣分。在区（镇）党委、纪委监察办发现查处之前，及时跟进主动处置（指适用第一种形态的情形）的，不予扣分或相应予以加分；主动报告相关问题（指第二种形态及以上的情形）的，减半扣分或不予扣分；涉及瞒报的，加倍扣分。违纪违规案件发生后，针对分管、联系及分管基层站所等违纪党员干部、公职人员，通过组织生活会、警示教育会落实警示教育制度，强化以案促改，健全内部制度规范，强化内部监督，有具体举措并见到实效的，适当予以加分。', '班子成员被查处采取第一种形态处置（不含诫勉）的扣2分，被诫勉的扣4分，被党纪政务轻处分（含组织调整）的扣8分，被党纪政务重处分及以上的扣16分，以上扣分按60%权重折算；分管领域、办所主要负责人、其他负责人被查处的，按前述情况每人次减半扣分（主动开展的第一种形态处置除外）；其他公职人员被查处的，按前述情况每人次的25%扣分（主动开展第一种形态处置的除外）；主动报告分管领域、办所属党纪政务轻处分问题（含组织调整）的，不予扣分，属党纪政务重处分问题的，减半扣分，涉及瞒报的，扣完16分。违纪违规案件发生后，对分管、联系及分管基层站所等违纪党员干部、公职人员，通过组织生活会、警示教育会落实警示教育制度，强化以案促改，健全内部制度规范，强化内部监督，有具体举措并见到实效的，适当予以加分，以上扣分、加分情况按40%权重折算', '违纪违规', 1, '追责评价', 16.00, 2022, b'1'),
	(22, '按要求召开“1+X”会议，分析本基层站所全面从严治党形势任务、问题风险，以及“七张问题清单”状况，明晰各方职责任务，研究解决履责突出问题，推进各方职责任务调整、完善及有效落实。', '站所按规定次数（全年4次）、规定时间（每季度次月10日前）召开，各主体提交审核通过职责任务、工作进展等。每少召开1次扣1分，各主体未提交审核通过职责任务、工作进展的，每个主体扣0.2分，最多扣1分（如未召开不再扣分）；每已超过规定时间（即每季度次月15日前）召开1次扣0.2分。', '“1+X”专题会议', 2, '明责评价', 4.00, 2022, NULL),
	(23, '坚持问题、风险导向，特别是依据全面从严治党、“七张问题清单”导向，查找、建立本基层站所各主体“问题清单”并全程跟进调整完善，同时有针对性地建立“措施清单”及依据上级移交、反馈、交办的“问题清单”有效、动态建立“措施清单”，确保明确履责方向、突出履责重点、强化履责协同、有效落实责任清单。', '站所各主体按规定条数（4条及以上）、规定时间（3月31日前，2022年为8月31日前）建立责任清单，每少1条扣2分（其中每少1条措施清单扣1分），扣完为止。', '建立责任清单', 2, '明责评价', 8.00, 2022, NULL),
	(24, '认真制定“第一议题”(含学规学纪)年度安排计划，各基层站所坚持每月不少于1次组织开展学习、宣传、教育、警示等活动，推动基层站所各主体做到“两个维护”，提高政治站位，强化纪律规矩意识，守牢法纪底线红线。', '站所按规定次数（每月不少于1次）、规定时间（每月月底前）执行落实“第一议题”制度，每少1次扣0.2分。', '“第一议题”制度', 2, '明责评价', 4.00, 2022, NULL),
	(25, '坚持问题、风险导向，依据本基层站所自我查找、建立的，以及上级移交、反馈、交办的“问题清单”及相应建立的“措施清单”等，全程动态推进整改进展直至整改完成销号，形成落实“整改清单”，实现各责任主体能动、协同履责工作到位，提升管党治党责任落实成效。', '站所各主体按规定时间（2022年12月31日前）完成问题清单整改销号，未完成整改销号1条扣2分，每已超出规定时间（即2023年1月10日前）完成整改销号1条扣1分。', '落实责任清单', 2, '履责评价', 16.00, 2022, NULL),
	(26, '根据“5+1”谈话机制要求，认真落实区（镇）“一把手”、纪委负责人、组织部门负责人或分管领导对基层站所“一把手”开展的日常谈话、任职谈话、监督谈话、集体谈话、勉励谈话，明确职责任务，落实问题风险防范整改和及时处置。', '站所主要负责人未认真执行落实区（镇）“一把手”、纪委负责人、组织部门负责人或分管领导对“一把手”日常谈话、任职谈话、监督谈话、集体谈话等，抓好问题风险防范处置的，每次扣1分，情形严重的扣2分。', '“5+1”谈话机制', 2, '履责评价', 2.00, 2022, NULL),
	(28, '围绕上级重大决策部署及基层站所党风廉政建设重点领域，“七张问题清单”所涉内容等，相关主体每季度不少于1次组织（联合）开展监督检查，加强问题、风险排查发现及有效处置、整改。', '	站所各主体按规定次数（全年4次、半年2次）、规定时间（每季度次月10日前，最迟2023年1月10日前）完成监督检查并提交，每少1次扣1分（2022年设为2次），扣完为止。全年累计发现并完成办理（交办）问题不少于2个（2022年为1个），每少1个扣1分，扣完为止。', '监督检查工作', 2, '履责评价', 4.00, 2022, NULL),
	(29, '严格按照区(镇）“三重一大”事项目录清单和议事规则规定要求，协助落实本基层站所涉区（镇）“三重一大”事项决策必要前置程序，自觉接受上级领导和有效监督，协助抓好相关问题整改。协助主动参与区（镇）“三重一大”事项决策监督。', '站所协助参与“三重一大”决策事项前期工作不到位、不规范致使事项审核出现问题、决策结果发生与前期工作关联问题的，按事项每次扣1分，扣完为止；协助执行通过决策事项出现问题的，按事项每次扣1分，扣完为止。', '“三重一大”决策', 2, '履责评价', 4.00, 2022, NULL),
	(30, '主动抓好监督执纪“四种形态”，基层站所相关主体主动适用第一种形态处置问题，主动发现、报告违纪违法问题。', '站所相关主体每年（12月31日前）不开展第一种形态处置的扣2分，同时发生被区（镇）党委、纪委及以上采取“四种形态”处置问题的扣2分，扣完为止；经发、村建、综合执法仅开展1人次第一种形态处置的扣1分，其他站所达到1次不扣分。', '第一种形态运用', 2, '履责评价', 6.00, 2022, NULL),
	(31, '基层站所各主体对照责任清单、协同履责绩效考评项目等，客观、如实、及时向区（镇）分管领导报告履责情况，特别是主动做好专题会议汇报及特殊情况随时汇报等，根据监督评议意见、“一把手”约谈意见及纪委情况通报等，抓好突出问题及风险整改落实。', '站所各主体每年不按规定向分管领导履责报告的，每次扣1分；遇特殊情况未做好随时报告的，扣2分；未按监督评议意见抓好整改，每次扣1分。', '履责情况报告', 2, '履责评价', 4.00, 2022, NULL),
	(32, '基层站所“一把手”每年年底区（镇）党委（扩大）会议上口头方式或书面方式述责述廉，把包括执行政治纪律和政治规矩、贯彻落实中央决策部署、履行管党治党责任、担当作为、工作实绩、履职用权、廉洁自律等方面，并接受评议。', '	站所“一把手”每年未在党委会议上述责述廉的（含书面），扣2分；评议结果为基本合格、不定等次的扣1分、不合格的扣2分。', '述责述廉评议', 2, '履责评价', 4.00, 2022, NULL),
	(33, '基层站所相关负责人，特别是“一把手”认真落实个人有关事项报告规定，如实、及时做好即时报告、年度报告工作。', '基层站所负责人不按规定报告个人事项的每次扣1分。', '个人有关事项报告', 2, '履责评价', 2.00, 2022, NULL),
	(34, '基层站所“一把手”要全面如实记录相关干部本人或授意他人插手干预干部选拔任用、土地使用权出让、工程建设、房地产开发与经营、执纪执法活动等重大事项情况并第一时间做好向区（镇）党委报告工作。', '基层站所“一把手”发生未如实记录报告领导干部插手干预重大事项的，每次扣1分，扣完为止。', '领导干部插手干预重大事项记录报告', 2, '履责评价', 2.00, 2022, NULL),
	(35, '基层站所相关负责人，特别是“一把手”认真抓好清廉站所建设，严格执行落实议事规则、集体决策机制及工程项目招投标制度规定等，严格执行行业、业务工作法律法规、政策制度规定，健全单位内部治理、内控、监督机制，全面规范权力运行，加强站所清廉文化建设，积极打造清廉单元建设示范标杆。', '站所发生未严格执行落实议事规则、集体决策机制及工程项目招投标制度规定等、未严格执行行业、业务工作法律法规、政策制度规定，未健全单位内部治理、内控、监督机制，未抓好清廉文化建设等情形的，每次扣1分。', '清廉站所建设度', 2, '履责评价', 8.00, 2022, NULL),
	(36, '基层站所相关负责人，特别是“一把手”认真抓好“一单三书”制度的执行落实，对所涉的各类问题、风险，各主体强化责任落实，协同落实整改。认真配合上级巡察、基层作风巡查、专项监督、专项治理等反馈问题的整改落实。', '站所各主体在规定时间内未抓好意见书、建议书、第一种形态告知书执行落实及反馈的，每次扣1分。', '“一单三书”', 2, '督责评价', 4.00, 2022, NULL),
	(37, '基层站所相关负责人，特别是“一把手”认真抓好涉分管领导的“纪委动议”制度的执行落实，对所涉的各类问题、风险及其他工作职责任务，各主体协同落实抓好整改及部署落实，防控风险、解决问题。认真抓好上级纪委专项监督、纪委专项治理及纪检监察建议书、正风肃纪移送函等按要求办理落实。', '站所各主体未在规定时间内根据分管领导要求抓好“纪委动议”事项执行落实及反馈的，每次扣1分，扣完为止。', '纪委动议', 2, '督责评价', 2.00, 2022, NULL),
