INSERT INTO `fr_measure` (`id`, `code`, `content`, `matter_id`, `progress`, `progress_status`, `update_time`) VALUES
	(1, '1', '1、进一步完善“非粮化”整治推进计划；\n2、建立完成目标任务倒排机制，突出节点化推进，责任逐层传导压实到村；\n3、加强日常监督检查跟进，抓好督查通报和考核结果运用。', 1, '完成“非粮化”整治目标任务，加强日常的监督检查，巩固整治成果。', 'DONE', NULL),
	(2, '2', '1、进一步建立健全面向各村的定期督查、考核通报机制；\n2、建立完善面向人居环境治理第三方主体的监督检查及考核奖惩机制，运用市场力量倒逼落实人居环境治理工作责任；\n3、加强宣传引导，推动建立农户主体参与房前屋后人居环境治理、规范垃圾分类的有效激励奖惩机制；\n4、进一步加强人居环境和垃圾分类执法力度，严厉打击、通报各类违规违法行为，形成震慑效果。', 2, '完善并落实面向各村的定期督查、考核通报机制，增强压力；加强对第三方主体的监督检查，细化考核奖惩机制，进一步压实治理责任；进一步动员村、第三方加强宣传引导，提高农户主体参与房前屋后人居环境治理、规范垃圾分类的主动性和积极性；抓住重点，加强人居环境和垃圾分类执法力度，严厉打击、通报各类违规违法行为，形成震慑效果。', 'DONE', NULL),
	(3, '3', '1、严格监管工程管理人员，对工程施工单位变更、追加项目等行为严格按程序审核，确保工程质量；2、加强日工程管理人员的日常教育管理，督促与工程单位人员“八小时”之外不接触、保持距离；3、严格自律，带头严格落实工程发包、合同签约、建设施工、验收、审价、资金支付、履约保证金缴纳等各环节监管，规避不良交往。', 3, '狠抓工程质量，严控程序、严管人员。严格监管工程管理人员，对工程施工单位变更、追加项目等行为严格按程序审核；加强工程管理人员日常教育管理，以身作则并督促工作人员与工程单位人员“八小时”之外不接触、保持距离；严格自律，带头严格落实工程发包、合同签约、建设施工、验收、审价、资金支付、履约保证金缴纳等各环节监管，规避廉政风险。', 'DONE', NULL),
	(4, '4', '1、严格兑现落实上级对各类农业主体的奖补规定，按程序规范操作，确保公平公正；2.对属于“三重一大”事项提交区（镇）党委会议决策后执行；3、主动规避与相关农业市场主体人员工作之外的交往。', 4, '按程序规范兑现落实上级对各类农业主体的奖补规定，确保公平公正；“三重一大”事项提交区（镇）党委会议决策后执行；避免与相关农业市场主体人员有工作之外的交往，规避廉政风险。', 'DONE', NULL),
	(5, '5', '1、加强农业农村办干部和公职人员队伍的教育监督，发现苗头性、倾向性问题主动落实第一种形态处置；2、定期结合站所会议，进行案例通报和警示；3、带头垂范，不接受农场主、工程单位的宴请、送礼等吃请，要求分管的干部职工对我本人进行监督。', 5, '加强农业农村办干部队伍的教育监督，定期结合站所会议进行案例通报和警示；发现苗头性、倾向性问题主动落实第一种形态处置；带头垂范，不接受农场主、工程单位的宴请、送礼等吃请，要求分管的干部职工、村干部对我本人进行监督。', 'DONE', NULL),
	(6, '6', '1、切实加强党性、廉政学习，提升自我修养；2、注意言行，重抓落实，不搞形式主义官僚主义这套。3、在各方面与上级要求保持一致。', 6, '明确廉政目标，切实加强党性、廉政学习，提升自我修养；以身作则，注意言行，管好身边人和身边事；重抓工作落实，杜绝形式主义官僚主义。', 'DONE', NULL),
	(7, '7', '1、注重自我警示，严格遵守不酒驾醉驾、不参与各类赌博活动；2、不去各类不良场所，净化朋友圈、社交圈；3.加强对分管条线的提醒和教育，注重以案为鉴，严防此类问题发生。', 7, '以身作则带头不酒驾醉驾，不参与各类赌博活动，不去各类不良场所，不搞圈子文化等；加强对分管条线的提醒和教育，注重以案为鉴，严防同类问题发生。', 'DONE', NULL),
	(8, '8', '"1、针对留言人蔡张丽所反映的情况，及时做好沟通，消除误解，取得蔡张丽对镇村防汛防台工作的支持与理解。\n2、区（镇）将根据此次事件反映出的问题进行完善提升，对防汛防台工作进行复盘，补齐救灾安置工作短板，进一步强化为民服务意识水平"', 8, '根据此次事件反映出的沟通问题、信息公开问题等进行完善提升，对防汛防台工作进行复盘，补齐救灾安置工作短板，进一步强化为民服务意识、提升服务水平。', 'DONE', NULL),
	(9, '9', '1、分别由分管拆违的副镇长叶吴良、分管农民建房的副镇长沈伟华对负责进行调查核实是否存在违建情况；\n2、根据调查结果，跟进处置。', 9, '程小唤属搬迁项目，2020年8月29日通过建房联审，2021年8月开始建房，建在规划点内，因建房政策暂停，采用备案制。', 'DONE', NULL),
	(10, '10', '指导、监督横港村做好海盐县百步镇常青家庭农场历年欠缴流转土地租金及水费工作，有效挽回经济损失和不良影响。', 18, '我办对横港村2013-2021年度土地流转租金发放，农户历年增减清单，土地征用面积，土地复垦面积比对核查。已核查清海盐县百步镇常青家庭农场在横港村历年流转面积底数，历年土地流转欠缴费用已核算清晰，共计应缴流转费用1587.5871万元，已交土地流转费用1482.8万元，实际欠缴104.7871万元。根据常青家庭农场实际欠缴土地流转费用，由该农场及投资人卜年兴作出承诺，分三次将欠缴的土地流转费用104.7871万元以转账方式分三次向横港村经济合作社补缴。2022年8月16日已完成第一笔土地流转费用补缴计20万元人民币，2022年8月21日已完成第二笔土地流转补缴费用30万元人民币，2022年12月20日前完成剩余的54.7871万元土地流转费用补缴工作。', 'DONE', NULL),
	(11, '11', '指导、督促横港村做好海盐县百步镇常青家庭农场2022年度实际承租流转土地面积的核定工作，指导横港村做好重新订立或变更流转土地合同、合同登记、风险保证金缴纳及规范“再流转”等工作。', 18, '通过前期调查发现，常青家庭农场存在“再次流转”问题。根据有关规定将对海盐县百步镇常青家庭农场名下“再流转”的种植户进行统一协商沟通，对于确实需要流转的“转包户”将根据实际耕种面积与其签定有效土地流转协议，及时建立大户档案。针对前期海盐县百步镇常青家庭农场转包行为和转包面积做好教育和引导退出机制。2022年12月底前我办将督促横港村按照2021年第三方测绘实际流转面积以及在2022年变更基础上与百步镇常青家庭农场重新签订土地流转合同，完成合同登记，风险保证金缴纳等工作。', 'DONE', NULL),
	(12, '12', '抓好举一反三，对包括横港村在内的各行政村流转土地实际租赁现状、合同履约、历年租金和水费缴纳、合同订立变更、保证金缴纳等情况开展一次全面的起底梳理、核查，指导、督促面上纠正土地领域的各类突出问题。', 18, '本轮共审计村级土地流转合同177份，涉及大户107户，流转总面积27974.973亩。问题存在主要有：1、部分村土地流转合同不规范。存在个别土地流转合同签订后未按规定到农经部门备案，存在规避监管的现象。2、存在大包户未经发包方同意随意转包且有改变原有用途的情况。主要是流转给外地人种藕，共发现10起。3、对搬迁、复耕等项目实施后净增的农地，存在发包给大包户时未签订补充合同或重新订立合同的问题。4、土地流转租金欠缴4户，合计金额1005690.8元。其中，农丰村项勤力欠缴488280元；新升村严建华142185元；胜利村百利家庭农场198305.51元、高本丰176920.29元。5、土地流转租金支出未按对应合同进行支付，租金支付情况进行匹配。发现问题的有7个村，涉及未能匹配金额960.89万元。其中：横港村540.11万元，百联村40.42万元，超同村122.98万元，农丰村10.44万元，胜利村71.61万元，逍恬村52.07万元，新升村123.26万元。 针对上述问题我办根据《海盐县人民政府办公室印发关于进一步健全农村土地经营权流转机制的意见的通知》盐政办发【2021】29号文件精神，要求各村落实四项整改措施，原则上在2022年12月底前整改到位。一是对不规范的土地流转合同及时变更、更正、备案；二是对恶意欠缴租金的大包户加强清缴力度，必要时走司法途径及时清缴；三是对农户已征收或退出的农地面积及时测量、更正；四是对土地租金收支不匹配的问题，督促相关村查清具体原因，作出详尽的书面情况说明并提供佐证资料（如大包户有因项目实施存在减免租金的村要提供相关政策、决策依据和情况说明等），对经核查确实存在欠缴租金等问题的督促予以追缴到位。 ', 'DONE', NULL),
	(13, '13', '深刻吸取教训，围绕土地流转领域易发多发问题，探索从进一步完善细化相关制度办法、确保制度办法刚性执行的层面推动实现土地流转工作常态长效管理，促进土地流转工作规范化，维护好村集体和广大农户利益。', 18, '农业农村办将深刻吸取教训，举一反三落实整改，认真落实《建议书》各项要求，积极推动实现村级土地流转工作规范化和常态长效管理，切实履行好村级“三资”和土地流转工作监督指导。', 'DONE', NULL),
	(14, '14', '抓紧做好海盐县百步镇常青家庭农场历年欠缴流转土地租金及水费工作，有效挽回经济损失和不良影响。', 19, '已核查清海盐县百步镇常青家庭农场在横港村历年流转面积底数，历年土地流转欠缴费用已核算清晰，共计应缴流转费用1587.5871万元，已交土地流转费用1482.8万元，实际欠缴104.7871万元。根据常青家庭农场实际欠缴土地流转费用，由该农场及投资人卜年兴作出承诺，分三次将欠缴的土地流转费用104.7871万元以转账方式分三次向横港村经济合作社补缴。2022年8月16日已完成第一笔土地流转费用补缴计20万元人民币，2022年8月21日已完成第二笔土地流转补缴费用30万元人民币，2022年12月20日前完成剩余的54.7871万元土地流转费用补缴工作。', 'PART_DONE', NULL),
	(15, '15', '抓紧做好海盐县百步镇常青家庭农场2022年度实际承租流转土地面积的核定工作，及时做好合同重新订立或变更流转土地合同、合同登记、风险保证金缴纳及规范“再流转”等工作。', 19, '通过前期调查发现，常青家庭农场存在“再次流转”问题。根据有关规定将对海盐县百步镇常青家庭农场名下“再流转”的种植户进行统一协商沟通，对于确实需要流转的“转包户”将根据实际耕种面积与其签定有效土地流转协议，及时建立大户档案。针对前期海盐县百步镇常青家庭农场转包行为和转包面积做好教育和引导退出机制。2022年9月底前横港村将根据2021年第三方测绘实际流转面积以及在2022年变更基础上与百步镇常青家庭农场重新签订土地流转合同，完成合同登记，风险保证金缴纳等工作。', 'PART_DONE', NULL),
	(16, '16', '抓好举一反三，对本村其他流转土地实际租赁现状、合同履约、历年租金和水费缴纳、合同订立变更、保证金缴纳等情况开展一次起底梳理、核查，全面纠正土地领域的各类突出问题，推动实现土地流转工作规范化和常态长效管理，切实维护好村集体和广大农户利益。', 19, '根据2021年第三方测绘版土地实际面积，横港村将在2022年9月完成村域内所有大户实际流转面积清查、核查。各大户2022年实际租赁流转面积将根据《海盐县人民政府办公室印发关于进一步健全农村土地经营权流转机制的意见的通知》盐政办发【2021】29号文件要求将重新签订土地流转合同，规范土地流转租金，水费，风险保证金的缴纳。', 'DONE', NULL),
	(17, NULL, '加强监管频率，执法倒逼', 20, NULL, NULL, NULL),
	(18, '18', '1、进一步提高政治站位，围绕中央决策部署，结合区（镇）实际，谋划细化政治监督项目，推动政治监督常态化、具体化；2、全程跟进监督项目问题风险处置工作，形成工作闭环，确保发挥监督实效；3、加强政治监督项目绩效评估和结果运用，倒逼提升监督质效。', 21, '按照推进政治监督项目化、具体化、常态化要求，2022年主要谋划实施了疫情防控、高质量发展、推进共同富裕、“非粮化”“非农化”整治等项目的政治监督，共计开展政治监督63次，助推了我区（镇）疫情阳性感染者“零发生”和获得半年度“红旗奖”。比如，还督促解决了横港村粮食功能内区转产种藕等非粮化整改500余亩等问题，收到了良好的成效。', 'DONE', NULL),
	(19, '19', '1、协助抓好系统应用工作指导和对各主体实际使用的督促工作，防止工作流于形式；2、跟进各主体系统应用绩效评估和反馈，确保工作落到实处。', 22, '今年以来，纪委监察办围绕落细落实“五张责任清单”、构建形成管党治党、清廉建设责任共同体，特别是聚焦加强对镇、村“一把手”和领导班子监督，协助党委专门开发上线运行镇、村一体“四责协同”监督应用系统，通过数字赋能推动破解对镇、村“一把手”和领导班子监督难题，助力我县“一体推进镇村四责协同”入选基层清廉（浙江）十大创新经验参选案例，《浙江日报》以《海盐用数字赋能精准监督 盯住关键少数 保障规范用权》为题予以报道。区分镇、村、中心站办3大类、12类责任主体，坚持以问题风险为导向，建立涵盖执行上级重大决策部署、履行职责、廉洁用权、落实中央八项规定精神、作风建设、遵守国家法律法规和党的纪律等方面的突出问题风险，明确整改措施、整改责任单位、责任人和整改期限，配套跟进问题风险分析研判、跟踪治理，确保全程闭环处置的责任清单制度，共生成建立责任清单1038份，涉及问题风险1120个，相应建立工作措施1351条，已经闭环处置问题风险725个。同时，聚焦各主体，特别是“一把手”履责用权监督，协助建立了“1+X”专题会议、“监督检查”、“5+1谈话机制”、履责约谈、“第一种形态运用”、“履责情况报告”、“述责述廉评议”、“一单三书”、纪委动议、三类主体考评、履责追踪预警等制度，促进责任层层传导、落实到位。今年以来，各主体累计开展履责约谈135人（次），“5+1”谈话24人（次），组织监督检查130次，履责情况报告151人（家）次，主动开展第一种形态处置14人（次），下达“一单三书”5份、纪委动议1次，对管党治党责任落实不力的1人予以追责问责，有效倒逼落实了责任。', 'DONE', NULL),
	(20, '20', '1、加强学习，提高政治“三力”，增强斗争精神，把“两个维护”落实到实际行动中去。2、进一步落实“三转”，聚焦主责主业，加强专业业务学习练兵；3、进一步抓好日常监督检查、专项监督，落实信访调查、问题线索处置工作，提高成案率，以案件查办形成有效震慑。', 23, '今年以来，认真履行监督专责，增强斗争精神，强化审查调查，全年共计查办案件16件 ，其中自办案件5件，在镇街道中并列第一。严肃查处了横港村土地流转领域漠视侵害群众利益案件，该村副书记俞某某、书记钱某某分别给予撤销党内职务和党内严重警告处分，抓好面上举一反三，该案例获得全市漠视侵害群众利益十佳案例。', 'DONE', NULL),
	(21, '21', '1、继续跟进专项监督治理，重点抓好农民建房、宅基地审批、民政慈善救助等领域的专项检查及优亲厚友等问题处置工作，切实解决好群众急难愁盼；2、通过包案等方式，推进漠视侵害群众利益事项处置解决。', 24, '今年以来，区（镇）纪委监察办始终坚持群众反对什么，就坚决防范和纠正什么，以专项整治漠视侵害群众利益问题、深入治理民生领域“微腐败”等为重点加强基层监督，督促职能部门和村（社）责任履行主体责任。结合百步实际，开展了村经济合作社运行问题专项治理，对涉及村合作社执行土地流转合同不到位、大包户欠缴租金、二次转包等问题，督促农业农村办抓好面上督查整改，我镇《深查 严督 实改，不让村集体流失一分钱》获评全市漠视侵害群众利益问题专项治理十佳案例。联动民政局、县派驻纪检组开展了城镇居家服务保障专项治理工作，百联村居家养老服务中心成为县内优质服务保障典型单位，提升了群众的获得感。同时，今年以来，纪委监察办继续抓好环境治理、拆迁拆违、安全生产、劳动用工、欠薪治理等涉及群众切身利益事项的监督工作，对发现的相关问题通过函告单等督促职能部门、相关村逐一落实了整改。', 'DONE', NULL),
	(22, '22', '牵头落实区（镇）纪委问题线索会商集体会商制度，加强问题线索管理。', 25, '加强问题线索统一管理，建立线索集体会商制度，确保今年以来的16件问题线索得到规范处置，其中转初核13件，通过第一种形态处置等予以了结2件，正在初核当中1件。', 'DONE', NULL),
	(23, '23', '1、加强纪委监察办内部教育警示；2、签订党风廉政建设承诺书；3、严格执行《规则》《规定》等，加强内部监督制约机制落实，严防灯下黑。', 26, '今年以来，认真落实打铁必须自身硬的要求，以更高标准、更严纪律打造基层纪检监察干部队伍。经常性与纪检监察干部开展谈心谈话，掌握思想动向、苗头倾向，跟进日常教育监督管理。切实强化内部监督，建立健全纪检监察干部廉政档案，开展“查风险、补短板”活动，认真落实纪检监察干部严禁吃喝的相关规定并作出公开承诺。', 'DONE', NULL),
	(24, '24', '1、经常性运用案例通报等形式加强内部警示教育；2、加强内部监督力度，切实防范接受可能影响公正履职的吃请等问题。3、落实实干举措，坚决克服形式主义官僚主义问题。', 27, '抓好警示教育，加强内部监督管理，防范接受管理服务对象吃请，作出公开承诺。', 'DONE', NULL),
	(25, '25', '切实提高政治站位，围绕区镇村社突出问题风险，强化政治监督、专项监督和日常监督检查谋划，抓好执行落实，确保发挥监督保障执行、促进完善发展作用。', 28, '坚持聚焦“三新一高”、“推进共同富裕”，以及“疫情要防住、经济要稳住、发展要安全”要求，强化政治监督，扎实推动中央和上级重大决策部署落地见效。加强疫情防控与经济社会发展有效统筹、协同高效监督检查，推动以干部的辛苦指数换取群众的平安指数和经济社会发展的高质量报表，确保了疫情“零发生”，获得了半年度“红旗奖”。抓好党的二十大等安保维稳专项监督，确保社会和谐稳定。深入开展粮食安全和耕地“非粮化、非农化”专项监督，共计督促完成整改1037亩。扎实抓好“六大提升行动”监督，开展G524国道征迁、园区“腾笼换鸟”专项整治、农村人居环境整治、助企纾困，以及集成家居时尚小镇、美丽城镇创建等重点工作专项监督。全年共计开展监督检查63次，查纠各类问题161个，发出专题通报15份，对在落实决策部署上不担当、不作为、慢作为等造成不良影响或损失的17人（次）党员干部、公职人员予以问责,其中给予党纪处分2人（次），坚决纠正有令不行、有禁不止行为，切实增强政治监督效果。以专项整治漠视侵害群众利益问题、深入治理民生领域“微腐败”等为重点加强基层监督，督促职能部门和村（社）责任履行主体责任。', 'DONE', NULL),
	(26, '26', '1、加强纪委监察办工作人员的日常监督管理；2、签订党风廉政建设承诺书；3、抓好示范带头，严守单位规章制度和效能建设规定，树立纪检干部良好形象。', 29, '加强日常监督管理，抓好日常提醒谈话，督促严守规范制度。', 'DONE', NULL),
	(27, '27', '1、落实经常性慎独慎微慎初慎小的内部警教育；2、层层开展提醒谈话；3、严肃处置违规违纪问题。', 30, '抓好内部警示教育，开展好谈话，未发生违规违纪问题。', 'DONE', NULL),
	(28, '28', '1、制订完善政治监督计划方案，落实项目化、常态化监督工作；2、跟进日常监督工作执行落实，加强自查工作；3、抓好评估和结果运用，推动监督落地。', 35, '区（镇）纪委监察办切实加强对政治纪律和政治规矩执行情况的监督检查，严肃党内政治生活，指导、监督10个村班子围绕重点工作推进、干部作风等专题召开组织生活会，认真开展批评和自我批评，抓好查摆问题整改落实，努力提高党内政治生活质量。加强政治生态建设情况分析研判，深化落实“四清单”“两档案”“两报告”制度，有力推动政治监督具体化常态化可视化。坚持聚焦“三新一高”、“推进共同富裕”，以及“疫情要防住、经济要稳住、发展要安全”要求，强化政治监督，扎实推动中央和上级重大决策部署落地见效。加强疫情防控与经济社会发展统筹工作监督检查，推动以干部的辛苦指数换取群众的平安指数和经济社会发展的高质量报表。抓好党的二十大等安保维稳专项监督，确保社会和谐稳定。深入开展粮食安全和耕地“非粮化、非农化”专项监督，共计督促完成整改1145亩。扎实抓好“七大提升行动”监督，开展G524国道征迁、园区“腾笼换鸟”专项整治、农村人居环境整治、助企纾困，以及集成家居时尚小镇、美丽城镇创建等重点工作专项监督。全年共计开展监督检查63次，查纠各类问题161个，发出专题通报15份，对在落实决策部署上不担当、不作为、慢作为等造成不良影响或损失的17人（次）党员干部、公职人员予以问责,其中给予党纪处分4人（次），坚决纠正有令不行、有禁不止行为，切实增强政治监督效果。', 'DONE', NULL),
	(29, '29', '1、进一步聚焦监督专责，切实抓好政治监督、日常监督和专项监督，加大案件查办力度，形成有效震慑；2、切实落实“民心是最大的政治”政治站位，加强纪检干部职业精神教育，树立为民执纪、为民监督理念，做到敢于、善于斗争，维护基层良好政治生态。', 36, '区（镇）纪委监察办“严”字当头，坚持有案必查、有腐必惩，始终保持高压态势，进一步加大案件查办力度，推动形成不敢腐的强大震慑。切实加大问题线索处置力度，集中力量核查专项检查、信访反映、公权力大数据监督预警等发现和移交的问题线索18件，其中转初核15件，通过第一种形态处置等已经予以了结3件。重点抓好村土地流转领域、农村人居环境治理领域党员干部、公职人员违纪违法案件的查处工作，严肃查办了横港村党委副书记俞某某违纪案件、镇行政执法办徐某某、沈某某监管失职案件等，全年共自行查办案件17件，给予党纪政务处分17人，挽回直接经济损失118.5871万元。区（镇）纪委监察办坚持以发现问题推动查补漏洞，以案件查办促进整改整治，以典型案例开展警示教育，深入做好“后半篇”文章，以点带面推动促改促治。聚焦风险领域，重点组织开展了村经济合作社运行领域、农村土地流转领域、“四位一体”环卫保洁领域、工程项目招投标领域等6个专项检查、治理，通过查摆剖析案件背后的深层次原因，用好纪检监察建议书、组织生活会、案例警示教育等有力武器，推动整改、促进监管、完善治理。全年向农业农村办、综合行政执法办等发出纪检监察建议3份，督促农业农村办等开展村经济合作社领域问题和土地流转合同履约专项检查审计，3个村专题召开组织生活会落实整改，结合区（镇）党委扩大会议等通报典型案件加强警示，面上发现纠正各类问题27个，完善健全制度规范3个。区（镇）纪委监察办认真落实打铁必须自身硬的要求，突出政治建设统领、思想建设引领和能力建设支撑，以更高标准、更严纪律打造基层纪检监察干部队伍。持之以恒学习习近平新时代中国特色社会主义思想，带头严肃党内政治生活，带头维护制度权威，带头增强斗争精神，推动纪检监察干部坚定理想信念、砥砺党性品格、忠诚履职尽责。通过参加素质能力提升班、纪检讲坛等途径，扩充专业知识，提升专业能力，养成专业作风。抓住区（镇）纪委监察机构规范化建设、强化片区协作机制和抽调跟班学习等契机，加强实战历练，有效提升了纪检监察干部监督检查、审查调查等专业能力。经常性与纪检监察干部开展谈心谈话，掌握思想动向、苗头倾向，跟进日常教育监督管理。切实强化内部监督，建立健全纪检监察干部廉政档案，开展“查风险、补短板”活动，认真落实纪检监察干部严禁吃喝的相关规定并作出公开承诺。', 'DONE', NULL),
	(30, '30', '、根据省委《关于规范领导干部廉洁从政从业行为进一步推动构建亲清政商关系的意见》等要求，专门制订执行“禁业清单”“负面清单”“限制清单”，从制度层面保障亲清政商关系良性构建；2、推进开发区公权力大数据监督应用建设，强化确保公权规范运行，促进亲清政商关系建设。3、持续跟进专项监督检查，查纠相关突出问题。', 37, '深化推进清廉开发区建设，以开发区公权力大数据管理平台落地为基础，加快架设运行以工程建设、招商引资、土地收购为主要内容的开发区公权力大数据监督“云哨”应用。应用架设运行以来，共计监督主体30个、监督项目200个、项目资金8.94亿元，处置预警88个，《浙江日报》等媒体专题报道了百步经开区公权力监督应用成效。坚持常态化开展明察暗访与元旦春节、国庆中秋等重点节点专项检查相结合，深挖细查各类违纪行为，重点开展了违规接受管理服务对象吃请和娱乐活动、收送礼品礼金、餐饮浪费，以及酒驾醉驾等问题的正风肃纪检查和专项治理工作，推动中央八项规定精神成为长期有效的铁规矩、硬杠杠。全年共开展各类检查15次，发现处置问题24个，批评教育帮助和处理14人，其中，对3名违规接受管理服务对象娱乐活动安排的公职人员予以党纪处分，对9名酒驾醉驾的党员干部予以党纪处分。', 'DONE', NULL),
	(31, '31', '1、协助党委严格督促落实管党治党责任落实应用系统运用工作，保障各项机制落实到位；2、重点加强三重一大决策、相互监督提醒、领导干部插手干预重大事项记录报告、个人重大事项报告及内部公开、述责述廉评议、向一把手报告履行一岗双责情况等制度执行落实的监督检查。', 38, '区（镇）纪委监察办加强对镇、村“一把手”和领导班子监督，结合区（镇）实际，协助党委专项订立4大类、28项的“三重一大”事项目录清单，重点推动建立从拟决策事项审核、决策过程监督，到决策事项执行跟踪的“三重一大”集体决策工作闭环，主动跟进督促落实“一把手”“五不直接分管”“末位表态”等制度，切实贯彻落实好民主集中制原则。纪委监察办围绕推动落细落实“五张责任清单”、构建形成管党治党、清廉建设责任共同体，协助党委专门开发上线运行镇、村一体“四责协同”监督应用系统，通过数字赋能推动破解对镇、村“一把手”和领导班子监督难题，助力我县“一体推进镇村四责协同”入选基层清廉（浙江）十大创新经验参选案例，《浙江日报》以《海盐用数字赋能精准监督 盯住关键少数 保障规范用权》为题予以报道。区分镇、村、中心站办3大类、12类责任主体，坚持以问题风险为导向，建立涵盖执行上级重大决策部署、履行职责、廉洁用权、落实中央八项规定精神、作风建设、遵守国家法律法规和党的纪律等方面的突出问题风险，明确整改措施、整改责任单位、责任人和整改期限，配套跟进问题风险分析研判、跟踪治理，确保全程闭环处置的责任清单制度，共生成、建立责任清单1038份，涉及问题风险1120个，相应建立工作措施1351条，已经闭环处置问题风险725个。同时，聚焦各主体，特别是“一把手”履责用权监督，协助建立了“1+X”专题会议、“监督检查”、“5+1谈话机制”、履责约谈、“第一种形态运用”、“履责情况报告”、“述责述廉评议”、“一单三书”、纪委动议、三类主体考评、履责追踪预警等制度，促进责任层层传导、落实到位。全年各主体累计开展履责约谈135人（次）、“5+1”谈话24人（次），组织监督检查130次，履责情况报告151人（家）次，主动开展第一种形态处置14人（次），下达“一单三书”5份、纪委动议1次，对管党治党责任落实不力的1人予以追责问责，有效倒逼落实了责任。', 'DONE', NULL),
	(32, '32', '1、加强内部监督工作，落实管党治党责任落实应用等工作；2、抓好纪检监察干部日常教育，特别是警示教育等工作；3、落实抓早抓小第一种形态处置。', 39, '区（镇）纪委监察办经常性与纪检监察干部开展谈心谈话，掌握思想动向、苗头倾向，跟进日常教育监督管理。切实强化内部监督，建立健全纪检监察干部廉政档案，开展“查风险、补短板”活动，认真落实纪检监察干部严禁吃喝的相关规定并作出公开承诺。', 'DONE', NULL),
	(33, '33', '针对村社减轻基层负担问题治理反弹循环问题，重点跟进对区（镇）相关条线、部门随意下达给村社的台账、牌子上墙等任务问题的监督检查，强化上位形式主义问题的统筹监督治理，重点解决好会多文多问题。', 40, '区（镇）纪委监察办积极跟进对区（镇）相关条线、部门随意下达给村社的台账、牌子上墙等任务问题的监督检查，强化上位形式主义问题的统筹监督治理，重点解决好会多文多问题。', 'DONE', NULL),
	(34, '34', '1、对全体纪检干部开展专题教育，并通过上挂锻炼等方式，提高业务水准和工作标准意识；2、抓好自办案件、信访调查处置等实战，抓好攻坚攻关，增强斗争精神和工作担当，培养职业精神。', 41, '区（镇）纪委监察办坚持把学懂弄通做实习近平新时代中国特色社会主义思想作为首要政治任务，通过区（镇）党委理论学习中心组学习、“第一议题”制度、县纪检讲坛、纪委监察办专题学习，以及集中夜学等，组织纪委监察办全体干部认真学习党的二十大精神、习近平总书记重要论述、重要讲话和重要指示批示精神，努力做到真信笃行，坚定正确政治方向，知行合一抓好贯彻落实。全年共计参加党委中心组学习会和县纪检讲坛学习17次，开展“第一议题”学习6次、纪委监察办专题学习9次。同时，纪委监察办严格执行请示报告制度，重大事项、重要案件等及时主动向县纪委监委和区（镇）党委请示报告。区（镇）纪委监察办认真落实打铁必须自身硬的要求，突出政治建设统领、思想建设引领和能力建设支撑，以更高标准、更严纪律打造基层纪检监察干部队伍。持之以恒学习习近平新时代中国特色社会主义思想，带头严肃党内政治生活，带头维护制度权威，带头增强斗争精神，推动纪检监察干部坚定理想信念、砥砺党性品格、忠诚履职尽责。通过参加素质能力提升班、纪检讲坛等途径，扩充专业知识，提升专业能力，养成专业作风。抓住区（镇）纪委监察机构规范化建设、强化片区协作机制和抽调跟班学习等契机，秦银平同志参加县纪委为期3个月的实战历练，有效提升了纪检监察干部监督检查、审查调查等专业能力。', 'DONE', NULL),
	(39, '39', '1.利用第三方人员加大巡查力度；\n2.发挥好联盟委员的联系作用；\n3.加大普法宣传和教育', 31, '1、全年利用第三方人员对全镇餐饮食品单位进行全面巡查；2、建立百步横港餐饮食品微信群，传达餐饮食品的各项最新要求；3、全年培训餐饮和食品店200多人次；4、联盟委员帮助巡查。', 'DONE', NULL),
	(40, '40', '1.注重工作实效；\n2.强化对标落实。', 32, '1、转变工作方法，结合到其他工作中去，不单是为了完成任务而完成任务，为其他工作打下基础；2、将任务进行细化分解，留有工作痕迹。', 'DONE', NULL),
	(41, '41', '1.加强学习；\n2.加强培训', 42, '1、加强学习上级会议精神或者文件精神，做到领会正确；2、对下要及时开会传达精神，并作工作布置细化分解。', 'DONE', NULL),
	(42, '42', '1.加强对标执行；\n2.加强理论学习', 43, '1、加强对八项规定精神学习；2、定期通报违纪案件进行警示。', 'DONE', NULL),
	(43, '43', '加强自我约束和修养', 44, '1、对照工作要求和工作纪律要求严格要求自己；2、八小时之外遵守党的八项规定精神。', 'DONE', NULL),
	(44, '44', '公正严格执法；设立熟人回避原则。', 45, '1、秉公执法，严格执行对应法律条款；2、针对熟人更换执法人员进行办案。', 'DONE', NULL),
	(45, '45', '加强自查自纠；落实底线要求。', 46, '1、根据工作要求细化工作的具体实施时间，尽量提前完成，不拖拉完成。', 'DONE', NULL),
	(46, '46', '加强修养；明确目标方向', 47, '1、学会心理调节放松心情；2、心理调整不过来寻求心理咨询的帮助。', 'DONE', NULL),
	(58, '58', '1.加强对两新党组织、村社党组织的调研走访；2.进一步开拓创新，结合百步实际，打造特色和亮点工作，提升百步基层党建工作美誉度和影响力；3.结合百步云工业社区运营，常态化开展两新党组织的指导和服务。', 73, '1.常态化走访两新党组织、村社党组织，开展调研，帮助协调解决相关问题；2.重点打造中达、金达、百联村、百步社区等党建示范点，结合自身特色，打造党建阵地和亮点工作；3.百步云工业社区安排专职社工，划分网格，常态化开展两新党组织的指导和服务。', 'DONE', NULL),
	(59, '59', '1、加强对党政办、招商和项目推进科等分管条线干部和职工的教育监督，发现苗头性、倾向性问题主动落实第一种形态处置；2、结合部门例会，常态化开展教育，进行案例通报和警示；3、带头垂范，不接受工程单位、服务对象的宴请，要求分管的干部职工对我本人进行监督。', 74, '1、加强对党政办、招商和项目推进科等分管条线干部和职工的教育监督，经常性开展谈心谈话，发现苗头性、倾向性问题及时进行提醒谈话；2、结合部门例会，开展教育4次，进行案例通报和警示教育等；3、带头垂范，不接受工程单位、服务对象的宴请，要求分管的干部职工对我本人进行监督。', 'DONE', NULL),
	(60, '60', '1.对招商项目尤其是拿地项目要严格把关，引进高质量符合百步产业要求的项目；2.保持“清亲”政商关系，既要服务好企业，又要保持距离；3.招商项目政策，按照上级规定执行，并确保程序规范到位。', 75, '1.对招商项目尤其是拿地项目严格把好质量关，经县项目联审会议通过、镇党委会议集体研究后，再签订投资协议并供地，投资协议对项目投入、产出进行明确要求并规定好违约责任；2.保持“清亲”政商关系，既服务好企业，又要保持距离；3.招商项目政策，按照上级规定执行，涉及“一企一策”政策的，确保程序规范到位。', 'DONE', NULL),
	(61, '61', '1.以镇“半月谈”为载体，经常性走访联系农户、企业，了解群众所思所想，宣传上级决策方针；2.对农户、企业反映的问题进行协调解决，暂时没法解决的要做好解释宣传。', 76, '1.以镇“半月谈”为载体，经常性走访联系农户，坚持每周走访企业，了解群众所思所想，宣传上级决策方针；2.对农户、企业反映的问题进行协调解决，今年以来已协调解决相关问题11个，暂时没法解决的要做好解释宣传。', 'DONE', NULL),
	(62, '62', '1.注重自我警示，严格遵守不酒驾醉驾、不参与各类赌博活动，不去各类不良场所，净化朋友圈、社交圈。2.加强对分管条线的提醒和教育，落实经常性慎独慎微慎初慎小的内部警教育，层层开展提醒谈话。', 77, '1.注重自我警示，严格遵守不酒驾醉驾、不参与各类赌博活动，不去各类不良场所，净化朋友圈、社交圈。2.加强对分管条线的提醒和教育，经常性开展慎独慎微慎初慎小的内部警教育，跟分管条线的中层干部、联系村书记开展提醒谈话3次。', 'DONE', NULL),
	(63, '63', '1、切实加强党性、廉政学习，提升自我修养；2、注意言行，重抓落实，不搞形式主义官僚主义这套。3、在各方面与上级要求保持一致。', 78, '1、切实加强党性、廉政学习，提升自我修养；2、注意言行，重抓落实，不搞形式主义官僚主义这套。3、在各方面与上级要求保持一致。', 'DONE', NULL),
	(64, '64', '　扎实推动疫情防控、民政、退役军人、三社、便民服务等条线工作，职责到人，加强监督。', 79, '　扎实推动疫情防控、民政、退役军人、三社、便民服务等条线工作，职责到人，加强监督。', 'DONE', NULL),
	(65, '65', '1、严格按程序规范操作，确保公平公正；2、杜绝与利益相关方的不正当交往。', 80, '1、严格按程序规范操作，确保公平公正；2、杜绝与利益相关方的不正当交往。', 'DONE', NULL),
	(68, '68', '坚持以人为本、注重预防，推进反腐倡廉，最大限度地维护人民群众利益。坚持改革创新、勇于实践，积极探索，不断创新预防腐败的新举措。坚持依纪依法、公开透明，保证廉政风险防控工作的严肃性和规范性。坚持突出重点、全面覆盖，以点带面，推进廉政风险防控工作不断深入。坚持务求实效、长效管理，一切从实际出发，既要实现有效监控，又尽可能简化程序，实现廉政风险防控工作常态化、长效化。', 82, '坚持以人为本、注重预防，推进反腐倡廉，最大限度地维护人民群众利益。坚持改革创新、勇于实践，积极探索，不断创新预防腐败的新举措。坚持依纪依法、公开透明，保证廉政风险防控工作的严肃性和规范性。坚持突出重点、全面覆盖，以点带面，推进廉政风险防控工作不断深入。坚持务求实效、长效管理，一切从实际出发，既要实现有效监控，又尽可能简化程序，实现廉政风险防控工作常态化、长效化。', 'DONE', NULL),
	(73, '73', '加强社会事务办全面从严治党主体责任，督促单位党员干部、公职人员各负其责、齐抓共管，严格执行上级部署决策', 83, '加强社会事务办全面从严治党主体责任，督促单位党员干部、公职人员各负其责、齐抓共管，严格执行上级部署决策', 'DONE', NULL),
	(75, '75', '1、加强民政办干部和公职人员队伍的教育监督，发现苗头性、倾向性问题主动落实第一种形态处置；2、定期结合站所会议，进行案例通报和警示；3、带头垂范，不接受救助对象、工程单位的宴请、送礼等吃请，要求分管的干部职工对我本人进行监督。', 85, '1、加强民政办干部和公职人员队伍的教育监督，发现苗头性、倾向性问题主动落实第一种形态处置；2、定期结合站所会议，进行案例通报和警示；3、带头垂范，不接受救助对象、工程单位的宴请、送礼等吃请，要求分管的干部职工对我本人进行监督。', 'DONE', NULL),
	(83, '83', '贯彻新发展理念，履职尽责、担当作为，聚焦招大引强、项目推进、经济发展、疫情防控、全面深化改革、维护社会稳定等正在做的工作，不折不扣把党中央和上级党委政府的决策部署落实到位。', 90, '今年以来，本人坚持贯彻高质量发展理念，聚焦招大引强、项目推进、经济发展、疫情防控、全面深化改革、共同富裕等重点工作和中心工作，不折不扣把党中央和上级党委政府的决策部署落实到位。', 'DONE', NULL),
	(84, '84', '1、按要求严格管理相关涉密文件。2、严禁网上传送各类涉密信息。3、加强相关人员的保密安全意识。', 91, '一、严格落实“四严四整”相关要求，领会到位、落实到位、检查督导到位。二、严格保密工作“6个管住”“40个严禁”，1、抓住社交软件和网络平台，2、管住信息发布与传递，3、管住手机使用，4、管住涉密文件，5、管住涉密办公设备，管住涉密场所。三、相关人员签订安全保密责任书。', 'DONE', NULL),
	(85, '85', '1、牢记法律法规、党纪党规。2、注重自我警示，不去各类不良场所，净化社交关系。3、加强对分管条线人员的提醒和教育，以身垂范。', 97, '一、思想上、行动上始终和党委、政府保持高度一直；二、加强党纪党规的学习，时刻以党章标准要求自己；三、强化自我约束，注重自我警示，养成良好的生活习惯；四、加强责任担当，严明工作纪律，杜绝一切违法违纪行为；五、做好模范示范带头作用；六、自觉接受组织和群众的监督。', 'DONE', NULL),
	(86, '86', '1、切实掌握合格青年的入伍动机和思想状况。2、严格征兵流程 ，扎实做好病史调查，全面了解合格青年身体状况。3、加强新兵入伍后思想交流和现实表现。', 92, '一、切实掌握双合格青年的入伍动机，强化役前适应性训练；二、加强与合格青年的思想沟通，彻底掌握青年的思想状况；三、严格征兵流程，切实做好病史调查，全面掌握合格青年的身体状况，检查以体检标准为纲要；四、加强新兵入伍后的思想交流，及时掌握入伍后的现实表现情况；五、加强与入伍青年家属的沟通和了解，积极鼓励入伍青年安心服役。', 'DONE', NULL),
	(87, '87', '1、加快摸排涉安违建，及时做好“一企一档”工作。2、对排摸到的涉安违建加快清理，消除安全隐患。', 93, '一、及时做好企业涉安违建排摸工作，建立“一企一档"；二、对涉安违建安轻重缓急有序推进拆违工作；三、加强部门协作，定期做好巡查，做好新增违建管控工作；四，加强与企业主的沟通交流，强调相关要求。', 'DONE', NULL),
	(88, '88', '1、严格落实廉洁征兵要求。2、加强应征青年的思想灌输，主动讲明征兵要求。3、签订各类廉洁征兵责任书。', 94, '一、严格落实廉洁征兵要求，相关人员签订廉洁征兵责任书。二、利用征兵宣传、目测初检等场合强化征兵工作要求；三、严格落实择优定兵原则，杜绝一切走关系、开后门等现象；四、加强征兵工作人员廉洁意识和定期谈话，强化思想责任。', 'DONE', NULL),
	(89, '89', '1、加强自身学习，提高处事解决问题的能力。2、与党委、政府在思想上、行动上保持高度一直。3、勇于担当，在急难险重中冲锋在前，发挥作用。', 96, '一、加强自身理论学习，严肃工作作风；二、在思想上、作风上、行动上始终与党委、政府保持高度一直；三、在工作勇于冲锋在前，积极做好带头作用；四、充分发挥不怕苦、不怕累的优良作风。', 'DONE', NULL),
	(90, '90', '1、加强自我警醒，时刻保持清醒状态。2、自觉接受组织和群众的监督。3、加强条线人员的管理，做好带头作用。', 95, '一、加强学习和自我警醒，时刻保持清醒头脑；二、与服务对象阐明组织纪律要求；三、接受组织和群众的监督；四、以身作则，起好模范带头作用。', 'DONE', NULL),
	(92, '92', '及时传达上级重大决策，严格按照规定的时间和程序执行重大决策。', 64, '第一时间传达上级重大决策，并按照规定的时间按步骤执行重大决策，保证执行过程不出差错。', 'DONE', NULL),
	(93, '93', '开展全员性的监管风险排查和经常性的警示教育，进一步强化基层一线监管人员的责任和履职意识。加强针对性教育培训，提高基层一线执法人员发现问题、解决问题的能力。', 65, '1、开展警示教育，加强工作人员的责任意识和履职意识；\n2、开展业务培训，提高工作效率。', 'DONE', NULL),
	(94, '94', '严格要求所有职工干部认真学习党员干部准则条例，加强法制教育，严明纪律。', 66, '1、定期开展法制教育，加强法制意识；\n2、对前期工作进行及时回顾与总结。', 'DONE', NULL),
	(95, '95', '1、结合会议强调中央八项规定精神；2、及时进行警示和通报。', 67, '1、定期召开会议强调中央八项规定精神；2、定期进行警示教育与通报其他单位工作人员违纪的后果。', 'DONE', NULL),
	(96, '96', '1、提高党员干部党性修养；2、行动到实处，不搞形式主义；3、在各方面与上级保持一致。', 70, '1、工作开展有布置工作、执行工作、总结工作3步骤；2、工作过程留有痕迹。', 'DONE', NULL),
	(97, '97', '1、强调党的纪律；2、落实开展好每半年一次的谈心谈话；3、严肃处理违法违规违纪问题。', 71, '1、半年一次谈心谈话；2、定期通报违法违纪案件结果；3、定期召开会议学习国家法律法规。', 'DONE', NULL),
	(98, NULL, '1、进一步提高政治站位，围绕中央决策部署，按照县局和区（镇）党委的要求，从分局工作职责和实际出发，抓好执行落实。', 105, NULL, NULL, NULL),
	(99, '99', '1加强与公安、检察部门的协同办案机制。有力打击一批违法分子。\n2.组织开展知识产权专项执法行动，查办一批商标侵权、虚假宣传等重点案件。\n3.加强商业秘密保护示范，指导协会和企业商业秘密保护建设，新建省级商业秘密\n保护基地 2个，市级商业秘密保护基地5 个，贯标企业 10 个。', 106, '1、今年办理2个知识产权案件，均移送公安；2、加强商业秘密保护示范，指导协会和企业商业秘密保护建设，新建省级商业秘密 保护基地 2个，市级商业秘密保护基地5 个，贯标企业 10 个。', 'DONE', NULL),
	(100, '100', '.根据疫情防控需要严格制定相关规定要求，及时指导各主体做好防疫工作。\n2.做好内部疫情防控管理。\n3.完善进口冷链食品和进口水果全链条信息追溯闭环管理。\n4.加强农贸市场、商超等重点场所防控。', 107, '1、抓好疫情防控，建立一支沿街商家网格员疫情专班队伍，进行每日防疫工作的检查及监督；2、建立农贸市场与药店的微信群，及时在线上或实地指导做好防疫工作；3、对上级通报的单位立即采取措施，填写疫情防控整改承诺书及停业等措施。', 'DONE', NULL),
	(101, '101', '1.构建企业培育库，遴选5 家企业组织开展各级政府质量奖创奖培育。\n3.培育 5家企业开展“品字标”浙江制造培育和“品字标”对标达标提升。\n4.积极对接上级部门，找准切入口，努力完成37件高附加值发明专利的任务。', 108, '1.构建企业培育库，遴选5家企业组织开展各级政府质量奖创奖培育。 3.培育5家企业开展“品字标”浙江制造培育和“品字标”对标达标提升。 4.积极对接上级部门，找准切入口，完成37件高附加值发明专利的任务。', 'DONE', NULL),
	(102, '102', '依托省局开发的“浙江特种设备在线”，对风险企业实施重点监管，对于较高风险和中风险企业加大“双随机”监督抽查比例\n2.加强特种设备领域执法力度，全年至少办结三起以上大案。。\n3.完善高风险隐患核销验收机制，对于采取停用、查封、注销等措施的，通过系统上传文书凭证和现场照片，确保风险隐患线上线下闭环处置。\n4.完善失信惩戒和守信激励机制，在特种设备生产、使用等环节依法开展列入严重违法失信名单等工作。', 109, '依托省局开发的“浙江特种设备在线”，对风险企业实施重点监管，对于较高风险和中风险企业加大“双随机”监督抽查比例 2.加强特种设备领域执法力度，全年办结三起案件。 3.完善高风险隐患核销验收机制，对于采取停用、查封、注销等措施的，通过系统上传文书凭证和现场照片，确保风险隐患线上线下闭环处置。 4.完善失信惩戒和守信激励机制，在特种设备生产、使用等环节依法开展列入严重违法失信名单等工作。', 'DONE', NULL),
	(103, '103', '1.每月按照县局下发的“五新月赛、五星月晒”竞赛活动方案细化工作目标，强化执行力度。\n2.定期总结分析，不断提升工作效能。', 110, '1.每月按照县局下发的“五新月赛、五星月晒”竞赛活动方案细化工作目标，强化执行力度，逐条对照，按期完成任务。 2.每月进行总结分析，不断提升工作效率。', 'DONE', NULL),
	(104, '104', '1、经常性运用案例通报等形式加强内部警示教育；2、加强内部监督力度，切实防范接受可能影响公正履职的吃请等问题。', 111, '1、定期运用案例通报等形式开展内部警示教育；2、加强内部监督力度，切实防范接受可能影响公正履职的吃请等问题。今年未出现接受监督管理对象吃请等“四风”问题。', 'DONE', NULL),
	(105, '105', '1、加强日常监督管理；2、签订作风建设承诺书；3、抓好示范带头，严守单位规章制度和效能建设规定，树立干部良好形象。', 112, '1、加强工作作风日常监督管理；2、每人签订作风建设承诺书；3、自己做好示范带头，严守单位规章制度和效能建设规定，树立干部良好形象。', 'DONE', NULL),
	(106, '106', '规范办案流程，切实提高办案质量。加强廉政教育，提高防腐能力。', 113, '1、办案人员定期接受培训；2、案卷审查通过县局审查；3、杜绝办案人员与当事人私下交流。', 'DONE', NULL),
	(107, '107', '加强警示教育，严肃处理违法违规违纪问题。', 114, '1、开展警示教育，提高法律意识；今年未发生酒驾、赌博等违法问题。', 'DONE', NULL),
	(108, '108', '围绕上级决策部署，结合疫情防控形势，逐级压实责任，一是加强对上级文件精神的传达学习和贯彻执行，提高疫情防控意识；二是强化监督，将防控措施落实落细；三是通过村社区、企业动员，加大奖励力度，加快推进新冠疫苗接种工作，筑起防疫屏障。', 115, '认真学习和传达上级文件；强化贯彻落实；在后疫情时期，更加强化对群众的服务，特别是强化接种工作，建立免疫屏障。', 'DONE', NULL),
	(109, '109', '加强学习，提高政治“三力”，提高对每一项为民服务事项的思想认识，提高敬畏心和责任心；经常性开展业务培训和学习，提升业务水平，杜绝漏报、错报等损害群众利益的情况。', 116, '树立正确的价值观，提升责任心；强化对相关同志的业务学习和审核，杜绝侵害群众利益。', 'DONE', NULL),
	(110, '110', '加强对工作人员的日常监督管理；抓好示范带头，严守单位规章制度和效能建设规定；改进群众工作方式方法，树立良好形象。', 120, '按照共产党员和公职人员要求，先做好自己；定期召开会议，强调纪律；接待群众事务中，做到耐心和认真。', 'DONE', NULL),
	(111, NULL, '1、进一步提高政治站位，积极贯彻落实中央决策部署，结合辖区实际，谋划细化政治监督项目，推动政治监督常态化、具体化；2、全程跟进监督项目问题风险处置工作，形成工作闭环，确保发挥监督实效；3、加强政治监督项目绩效评估和结果运用，倒逼提升监督质效。', 117, NULL, NULL, NULL),
	(112, NULL, '深化一室三队改革，建立全面完善的管理机制并强化8小时以外的管理；加强八项规定等制度的学习。', 122, NULL, NULL, NULL),
	(113, NULL, '1、加强政治学习，提高政治“三力”，把“两个维护”落实到实际行动中去。2、进一步落实“三转”，聚焦公安主业，加强专业化学习、练兵；3、进一步抓好日常监督检查、专项监督，落实信访调查、问题线索处置工作，提高成案率，提升群众满意度', 123, NULL, NULL, NULL),
	(114, NULL, '提高对各类矛盾纠纷的调解能力，努力实现矛盾纠纷及时就地化解，共建和谐邻里平安百步', 124, NULL, NULL, NULL),
	(115, NULL, '探索创新反诈宣传新模式，力争反诈宣传全民覆盖，激发全民反诈热情，从而降低发案', 125, NULL, NULL, NULL),
	(116, NULL, '1、坚决按规矩办事，按法律办事，讲原则、讲纪律；2、签订党风廉政建设承诺书；3、严格执行公安部、省厅、市局等规定，加强内部监督制约机制落实，严防灯下黑。', 126, NULL, NULL, NULL),
	(117, NULL, '自觉接受组织、班子成员的监督，畅通民主渠道、民主决策、民主议事', 127, NULL, NULL, NULL),
	(118, NULL, '1、切实加强党性、廉政学习，提升自我修养；2、注意言行，重抓落实，不搞形式主义官僚主义这套。3、工作、生活方面与上级要求保持一致。', 128, NULL, NULL, NULL),
	(119, NULL, '1、注重自我警示，严格遵守不酒驾醉驾、不参与各类赌博活动；2、层层开展提醒谈话；3、.加强对分管条线的提醒和教育，注重以案为鉴，严防此类问题发生。', 137, NULL, NULL, NULL),
	(120, '120', '1、进一步细化分工，明确责任。2、以周例会的形式进行推进。3、与条线的年终考核相挂钩。', 121, '把目标任务分解到月、周，以每周一上午例会形式进行推进。', 'DONE', NULL),
	(121, '121', '1、压实经发办等主体责任，加大检查的频率和力度。2、综合执法队加大处罚力度。3、定期以百步平安发布的形式，曝光安全生产存在隐患多的企业！', 129, '压实经发办主体责任，加大检查的力度，同时以大综合一体化执法为契机，对问题严重及整改力度不大的企业进行处罚。', 'DONE', NULL),
	(122, '122', '1、加强与征迁部门的联系，深入到被征迁群体，了解思想动态。2、压实征迁部门及村的责任，确保部门及村对信访工作的重视。3、做好预案和应急力量的准备。', 130, '定期召开问题隐患排查会议，对群众反映多的问题专班开会研究，及时回复群众。', 'DONE', NULL),
	(123, '123', '1、加强综合执法队业务学习，加强队伍管理。2、重大案件集体讨论决定。', 131, '形成定期学习制度，不定期进行谈话。', 'DONE', NULL),
	(124, '124', '1、加强全镇干部对信访条例的学习。2、压实条线特别是分管的条线领导的责任，对每一起初信初访都要以三到位的要求来办理。3、落实倒查机制。', 132, '加强信访隐患的排查，对每一起信访事项都落实分管领导牵头办理。', 'DONE', NULL),
	(125, '125', '1、加强队伍的教育监督，发现苗头性、倾向性问题主动落实第一种形态进行处置。2、率先垂范，带头做好示范。3、加强谈心谈话，正面做好引导。', 133, '对分管的条线负责人经常性的谈话提醒，同时严格要求自己。', 'DONE', NULL),
	(126, '126', '1、加强队伍教育，从思想上进行统一。2、加强二级考核，杜绝出现老好人思想。3、加强谈心谈话，对发现的问题及时指出。', 134, '经常性开展工作落实情况的检查，对发现的问题纳入考核。', 'DONE', NULL),
	(127, '127', '1、平时注重自我警示，严格遵守不酒驾醉驾、不参与各类赌博活动。2、不去各类不良场所，净化朋友圈、社交圈。3、加强对分管条线人员的谈话提醒，特别是重点人员。', 135, '经常性开展谈话提醒。', 'DONE', NULL),
	(128, '128', '1、加强学习教育。2、对布置的工作落实情况过程跟踪。3、在评先评优时有所体现。', 136, '加大检查力度，以周例会形式进行推进。', 'DONE', NULL),
	(129, '129', '对发展对象预审、联审；按要求谈话；村社书记列席区（镇）党委会', 98, '今年发展党员11人，均落实预审、联审；由本人组织谈话；党委会上讨论各村发展对象时，由村书记列席会议并介绍相关情况；党委委员对发展对象实行票决。', 'DONE', NULL),
	(130, '130', '经常性分层分类开展谈心谈话；严格按照干部选拔任用要求提拔干部；开展新提拔干部“回头看”。', 99, '经常性分层分类开展干部谈心谈话，中层干部谈话全覆盖。严格按照干部选拔任用相关规定提拔干部，于今年12月开展新提拔中层干部“回头看”。', 'DONE', NULL),
	(131, '131', '制定民情“半月谈”活动方案；定期下村入户入企走访。', 100, '策划民情“半月谈”活动，要求中层正职以上干部每月两次入企入户开展走访调研。截止目前，已累计走访农户513家，收集民情民意116条，当场答复87条、整理交办29条，办结29条。', 'DONE', NULL),
	(132, '132', '按照党章规定规范组织党员活动；按照相关要求规范开展党员关心关爱活动；一万元以上的党费使用经区（镇）党委会同意。', 101, '要求各党组织按照党章规定规范组织党员活动，落实“一本通”记录。开展党员关心关爱活动时注重摸排，关注覆盖面，同时，一万元以上的党费使用经区（镇）党委会研究同意。', 'DONE', NULL),
	(133, '133', '通过系统培训加强村社干部素养；经常性开展警示教育、提醒谈话等形式多样的教育活动。', 102, '组织村社干部“共同富裕示范样板”培训班，通过思想政治教育、业务条线培训等全方位提升村社干部素质能力。', 'DONE', NULL),
	(134, '134', '1、加强特种设备专业知识学习；2、在日常工作中加大巡查力度，做好执法必严、违法必究，有法可依，有法必依。', 33, '1、每月进行特种设备专业知识学习；2、在日常工作中加大巡查力度，尽早发现问题，解决问题。', 'DONE', NULL),
	(135, '135', '2、1、进一步增强责任意识，努力认真完成；2、做好示范带头，严守单位规章制度和效能建设规定。', 34, '1、加强责任意识；2、将上级布置工作要求解读明确，并根据本局工作结合开展。', 'DONE', NULL),
	(136, '136', '3、1、进一步提高政治站位，围绕上级决策部署，结合单位实际，谋划细化工作；2、加深学习，学好学透，执行落实到位。', 48, '1、进一步提高政治站位，围绕上级决策部署，结合单位实际，谋划细化工作；2、加深学习，学好学透，执行落实到位。', 'DONE', NULL),
	(137, '137', '1、签订党风廉政建设承诺书；2、认真学习相关《规则》、《规定》等；3、把好廉政关，杜绝请吃喝。', 49, '1、不接受私下管理对象宴请；2、以八项规定对自我严格要求约束。', 'DONE', NULL),
	(138, '138', '1、拉紧脑中的弦，时刻提醒自身；2、多学习一些案例通报等，提升自身觉悟；3、对上级的相关规定等，严格执行落实到位。', 50, '1、及时贯彻落实各项会议精神或讲话精神；2、学习些案例通报等，提升自身觉悟；3、对上级的相关规定等，严格执行落实到位。', 'DONE', NULL),
	(139, '139', '6、1、落实经常性慎独慎微慎初慎小的内部警示教育；2、抓好自身建设，带头加深学习，严守法律法规和纪律。', 51, '1、加强学习，定期学习各项工作相关的法律法规，提高法律修养。', 'DONE', NULL),
	(140, '140', '1、不断提升自身站位，时刻绷紧弦，在日常的工作中注重形象建设，带头做好示范；2、在平时工作中积极开拓进取，增强创新意识。', 52, '1、对原有和新的工作创新工作方式方法，运用适合自己单位的方式，结合实际开展工作。', 'DONE', NULL),
	(141, '141', '1.尽快完成老的保洁公司费用清算；2.进一步完善考核办法，做到月月有考核，定期有通报，时时有问题发现和交办机制。并在保洁合同中进一步明确权责；3.紧盯上级考核，进一步灵通信息。', 142, '完善了考核办法，逐月开展考核。每周开展环境卫生检查，成效较为明显。', 'DONE', NULL),
	(142, '142', '1.加快工业园区雨水排口、周边河道监控设施安装联网，8月上旬完成找头边；2.加强日常检测设备的维护及道路洒水作业；3.加大高污染高能耗及影响生态环境问题的查处力度。', 143, '开展89家印刷园区企业VOC排放颗粒状活性炭吸附摸底。每日开展市区道路洒水作业。', 'DONE', NULL),
	(143, '143', '1.创新信访积案化解方式；2.给与信访干部保护关爱；3.加强与长期信访人员的走访沟通。', 144, '及时向领导及党委反映处置信访的有关情况，通过法院调解能方式予以补偿。', 'DONE', NULL),
	(144, '144', '1.严格程序规范操作，确保公平公正；2.加强自我约束、自我管理，始终筑牢思想堤坝和自我防范意识，构建亲清政商关系；3.对“三重一大”事项提交党委会决策，不擅自决断。', 145, '建立亲清政商关系，2022年将环境卫生相关十几个合同规整为一个，强化规范化管理，预计本年度节约环境卫生相关经费400万元。11月，区（镇）党委对分工也作了调整。', 'DONE', NULL),
	(145, '145', '1.加强自身及条线公职人员的教育监督，发现问及时处置；2.定期结合周一例会、专题会议等，进行工作通报、案例警示等；3.坚持原则，不突破底线。', 146, '始终能管好自己，不存在违反“四风”情况。但没有很好管理好下属人员，导致今年7月15日分管条线人员参与有偿陪侍被纪委查处。', 'DONE', NULL),
	(146, '146', '1.牢固树i立事情处置，今天再晚也是早，明天再早也是晚的紧迫感，对问题不回避、矛盾不上交；2.注重个人言行，不断加强自身修养；3.增强处理复杂问题的能力。', 147, '以战斗的状态投入工作，取得了较好的成效。', 'DONE', NULL),
	(147, '147', '1.加强判断信息的敏锐性，严格材料信息的传输流程；2.管好自己的口、手及身边的人，不该说的坚决不透露任何信息；3.加强对敏感信息泄露应急处置能力。', 148, '高度关注，时刻防范，没有发生泄密等事情。', 'DONE', NULL),
	(148, '148', '1.牢记“嘉兴无小事、事事连政治”的要求，进一步提高工作站位，提高工作的政治敏锐性；2.跳出条线看条线，在工作大局中谋划分管条线的工作；3.以身作则，带好队伍，拉高标杆树立“向我看齐”的意识。', 139, '始终站在全局中谋划分管或协管条线的工作，今后长期坚持。', 'DONE', NULL),
	(149, '149', '1.学会“十个指头弹钢琴”，合理安排好工作；2.提高思想认识，消极到最后的代价可能更大，消极应对不如主动出击；3.强化团队建设，激发团队积极性和创造性。', 140, '能合理安排工作，发挥团队的主动性。今年11月，党委针对个人实际情况，分工做了调整。', 'DONE', NULL),
	(150, '150', '1.紧盯考核指标，每周进行研判会商；2.加强对上对接，熟练掌握考核规则；3.压实责任，将工作细化到最小颗粒度；4.创新工作方式方法。', 141, '今年7月百步设立了交警中队，加大日常巡查。交通事故无新增。同时，每周开展例会研判落后指标，针对性制定整改措施。', 'DONE', NULL),
	(151, '151', '通过集中学习、开展自我批评、谈心交流等方式，提高思想站位，做到警钟长鸣', 149, NULL, NULL, NULL),
	(152, '152', '上级精神及时传达部署，定期召开工作进度会议', 150, NULL, NULL, NULL),
	(153, '153', '1、加强对分管领域干部和公职人员的日常教育监督管理；2、落实常态化警示教育措施；3、以身作则，带头严守中央八项规定精神，树立良好形象。', 118, '扎实做好各项工作；强化对分管领域同志的监督教育；以党员身份，强化示范引领。', 'DONE', NULL),
	(154, '154', '1、落实“腾笼换鸟”工作，做优做强存量企业；2、对集成家居、印刷及装备制造三大传统产业开展调研，出台改造提升方案；3、围绕上级中心工作，建立完成目标任务倒排机制，突出节点化推进，责任逐层传导压实到人。', 152, '1.积极推进腾笼换鸟政策修订和协议的拟定；2.完成产业提升改造方案；3.压实责任，目标责任制考核指标有序推进', 'DONE', NULL),
	(162, '162', '1、严格按照政策要求，向符合条件的对象进行政策宣讲，做到宣传到位；2、对申报的材料，按申报条件逐级审核，相关经手人签名确认；3、对涉及金额较大的，提交镇长办公会议甚至班子会审议通过；4、杜绝徇私枉法，杜绝接受人情关系，做到客观公正。', 155, '在工作中扎实落实相关要求，对申报的材料开展了仔细核查', 'DONE', NULL),
	(163, '163', '1、进一步建立健全企业安全生产检查督查机制，压实企业责任；2、充分利用安全管家的专业性，开展针对性指导服务；3、加强宣传引导，提升生产主体的安全意识；4、联动安全执法，进一步加强执法力度，严厉打击各类违法行为，形成警示震慑效果。', 153, '严格落实安全生产的相关工作要求，企业主体安全生产意识明显提升，执法水平加强', 'DONE', NULL),
	(164, '164', '1、进一步压实市场监管分局职责，做到有法必依，执法必严，管理到位；2、结合工作职责，制定相关规章制度，规范执法行为；3、坚持“一把尺”执法，做到公平公正；4、及时向上级部门汇报，寻求上级部门专业指导。', 154, '1.坚持执法必严，结合各项工作要求，落实管理责任；2.坚持公正执法，依法办事；3.对上积极沟通，完善执法能力和水平', 'DONE', NULL),
	(165, '165', '1、加强经发办、市场监管分局、环保中队干部和人员队伍的教育监督，发现苗头性、倾向性问题主动落实第一种形态处置；2、定期结合工作会议，进行案例通报和警示；3、带头做好表率，不接受企业单位等管理服务对象的吃请和送礼等问题。', 156, '1.对分管条线开展了监督和教育；2.加强了廉政教育，做好带头表率', 'DONE', NULL),
	(166, '166', '1、强化理论学习，切实加强党性、廉政学习，提升自我修养和作风水平；2、注意个人及分管条线干部职工的言行，重抓落实，不搞形式主义官僚主义；3、在各方面与上级要求保持一致，并做到与时俱进。', 157, '1.严格加强理论学习和提升作风修养；2坚决和上级各项工作要求保持高度一致', 'DONE', NULL),
	(167, '167', '1、注重自我警示，严格遵守不酒驾醉驾、不参与各类赌博活动；2、不去不该去的地方，不去各类不良场所，净化朋友圈、社交圈，注意自身形象；3.加强对分管条线的提醒和教育，注重以案为鉴，严防此类问题发生。', 158, '1.未开展任何违法违纪行为；2做到对分管条线的提醒和教育', 'DONE', NULL),
	(168, '168', '1、严格落实上级以及镇级对各类企业的政策，按程序规范操作，确保公平公正；2、主动规避与相关企业管理层工作之外的交往；3、经常性谈话、交流提醒经发条线、环保条线等人员注意廉洁用权，杜绝谋私立；4.对属于“三重一大”事项提交区（镇）党委会议决策后执行；。', 159, '1.严格落实相关政策要求；2.对站办人员建立党风廉政谈话机制，传导廉政精神；3、三重一大事项均提交党委会议决策；4.主动规避了工作以外的各类交往', 'DONE', NULL),
	(169, '169', '1.加强学习，在服务业相关工作上学懂弄通；2.增强执行力，强化对该项工作的落实；3.责任层层传导，节点化推进相关工作；4.加强工作指标跟踪落实', 160, '严格落实在考核指标方面的工作，形成周例会制度，扎实推进各项工作', 'DONE', NULL),
	(170, '170', '1.加强对协议签约相关条例的学习，谨防范低级错误；2.协议拟定后由相关职能部门及法律顾问确认无误后再行签署；3.对项目、中介机构进行充分调研，合同条款，特别涉及政策条件的条款进行反复确认和沟通。', 161, '1.相关政策条例已认真学习；2.严格按照相关要求签订协议；', 'DONE', NULL),
	(171, '171', '1.严格监督和党内互相监督，做到时时自省；2.时刻站在政治的角度看待、解决问题，做好企业正当的服务工作，避免不良往来；3.规范自身及下属的工作、生活往来，严格按规定、要求开展工作', 162, '1.时刻自省自查；2.坚持初心，做好企业服务本职工作', 'DONE', NULL),
	(172, '172', '1.带头坚决杜绝违规吃喝等“四风”问题，加强党内和条线内互相监督；2.坚持接受廉政教育，熏陶廉政思想，时刻保持清醒的头脑；3.形成监督机制，发现苗头立刻处置', 163, '1.在四风问题上严格把关，未犯相关错误问题；2.较好形成互相监督的体制机制', 'DONE', NULL),
	(173, '173', '1.加强自身党性修养；2.加强廉政教育学习；3.坚决执行相关规章制度，严格要求自己', 164, '严格按照措施要求自己，廉政教育学习深入，党性修养有较大提升', 'DONE', NULL),
	(174, '174', '1.认真学习相关法律法规，严格规范自身行为；2.严格落实规范违法违纪行为，层层传导，压实责任；3.严肃处理违法违纪行为', 165, '压实责任要求，深入学习相关要求', 'DONE', NULL),
	(175, '175', '进一步提高执行上级相关工作任务过程中执行的时效性、实效性', 167, '全面排查上级相关工作任务，对可能存在的时效性、实效性问题即刻改正，并防止该类问题再次发生', 'DONE', NULL),
	(176, '176', '加强对干部队伍、公职人员队伍教育管理监督', 173, '对站办全体人员进行约谈，提高相关意识，日常做好管理', 'DONE', NULL),
	(177, '177', '严格执行落实构建亲清政商关系相关规定要求，杜绝利益输送、权力寻租、徇私枉法等问题、风险。', 174, '对该工作进行布置和强调，若发现的相关问题及时按相关制度处理，在日常的工作中严格执行落实相关规定', 'DONE', NULL),
	(178, '178', '严格执行落实中央八项规定精神，日常加大排查学习力度', 175, '全面排查可能存在的公款吃喝、接受管理服务对象吃请、娱乐活动及安排的游玩活动、公车私用、私车公养、违规报销或者转嫁应由个人承担的费用，以及其他形式主义、官僚主义等问题风险。要求站办所有人员严格执行落实中央八项规定精神', 'DONE', NULL),
	(179, '179', '加强作风建设，定期开展相关学习活动。', 176, '全面排查所有人员作风问题，日常经常性开展排查和教育，保持队伍好作风', 'DONE', NULL),
	(180, '180', '加强法律法规和纪律方面的学习，日常加强相关排查工作。', 177, '全面排查可能存在的违反国家法律法规和党的纪律方面的问题或风险。日常通过会议、个别谈话等方式加强相关教育和监督', 'DONE', NULL),
	(181, '181', '加强上级交办工作督查力度，及时高效完成工作。', 178, '全面梳理坚持上级交办工作，对存在进度缓慢等问题的工作罗列台账，明确时间点抓紧督促完成', 'DONE', NULL),
	(182, '182', '加强党性学习，提高政治站位，高效完成工作。', 179, '每月开展二十大精神等相关内容的学习，全面提高站办人员责任感', 'DONE', NULL),
	(183, '183', '加强督导，提高执行上级相关工作任务过程中执行的时效性、实效性', 180, '立即、高效执行上级相关工作任务', 'DONE', NULL),
	(184, '184', '加强对干部队伍、公职人员队伍教育管理监督', 181, '对干部队伍、公职人员队伍进行全方位实时教育管理监督', 'DONE', NULL),
	(185, '185', '日常加强学习，严格执行落实构建亲清政商关系相关规定要求，杜绝利益输送、权力寻租、徇私枉法等问题、风险。', 182, '严格执行中央八项规定，落实构建亲清政商关系相关规定要求，杜绝利益输送、权力寻租、徇私枉法等问题、风险。', 'DONE', NULL),
	(186, '186', '日常加强教育学习和相关排查力度，严格执行落实中央八项规定精神。', 183, '全面排查、实时监督各类其他形式主义、官僚主义等问题风险。', 'DONE', NULL),
	(187, '187', '加强法律法规和纪律方面的学习，日常加强相关排查工作。', 184, '全面排查、实时监督违反国家法律法规和党的纪律方面的问题或风险。', 'DONE', NULL),
	(188, '188', '加强上级交办工作督查力度，及时高效完成工作。', 185, '及时、高效完成上级交办工作', 'DONE', NULL),
	(189, '189', '日常加强党性学习，提高政治站位，高效完成工作', 186, '加强责任感，高效保质完成工作', 'DONE', NULL),
	(190, '190', '加强作风建设，定期开展相关学习活动', 187, '加强作风建设，提高意识', 'DONE', NULL),
	(191, '191', '1、落实经常性慎独慎微慎初慎小的内部警教育；\n2、层层开展提醒谈话；\n3、严肃处置违规违纪问题。', 200, '一是遵守规章制度，弘扬正气；二是以案为鉴，时刻警醒；三是坚持高压态势，防微杜渐。', 'DONE', NULL),
	(192, '192', '加强统筹，明确各村工作任务，每月检查一次以上。2、强化闭环管理，确保整改到位。坚决克服重检查、轻整改，重眼前轻长远等问题。3、强化督导，务求实效。', 194, '一是落实三级巡查机制，明确责任人；二是抓好宗教场所每月一次的检查，每半年一次的巡查；三是发现问题立即整改，建立长效机制。', 'DONE', NULL),
	(193, '193', '1、加强学习，提高政治“三力”，增强斗争精神，把“两个维护”落实到实际行动中去；\n2、依托侨台联、知联会、乡贤理事会、少数民族工作室创新性开展工作；\n3、创新工作争取在全县面上推广，争取市、省、国家级统战荣誉。', 195, '一是加强指导，夯实统战基层工作基础；二是搭建平台，助力共同富裕；三是加强阵地建设，提升统战服务能力。', 'DONE', NULL),
	(194, '194', '1、积极与上级部门沟通协调，任务指标合情合理；\n2、研究制定工作方案，细化任务，责任到人；\n3、按照项目化、节点化动态跟踪实施。', 196, '一营造招商氛围，明确目标任务；二是确定招引方向，构筑产业集群；三强化服务保障，助推项目落地。', 'DONE', NULL),
	(195, '195', '1、加强内部教育警示；\n2、签订党风廉政建设承诺书；\n3、严格执行《规则》《规定》《条例》，加强内部监督。', 197, '一是抓好廉政责任提醒谈话；二是切实构建亲清政商关系；三是以身作则，廉洁从政。', 'DONE', NULL),
	(196, '196', '1、加强工作人员的日常监督管理；\n2、签订党风廉政建设承诺书；\n3、抓好示范带头，严守单位规章制度和效能建设规定，树立统战干部良好形象。', 199, '一是加强学习，提高素质；二是开展进取，提高工作能力；三是自我完善，强化履职。', 'DONE', NULL),
	(197, '197', '1、经常性运用案例通报等形式加强内部警示教育；\n2、加强内部监督力度，切实防范接受可能影响公正履职的吃请等问题。\n3、落实实干举措，坚决克服形式主义官僚主义问题。', 198, '一是坚定理想信念；二是树立大局意识；三是构建亲清政商关系；四是廉政履职。', 'DONE', NULL),
	(198, '198', '1、打通协同推进全过程人民民主的有效路径;2、加快基层单元、代表联络站、室、点建设；3、充分发挥人大代表引领带动作用。', 204, '完善代表联络站、室、点建设，健全吸纳民意、汇集民智的工作机制，推进“三亮三进”工作，稳妥有序推进县、镇人大代表亮出身份、承诺，促进代表作用发挥。', 'PART_DONE', NULL),
	(199, '199', '1、加强代表履职能力培训；2、创新人大监督方法，提升监督效能，推动上级决策部署落地见效；3、依法维护和保障代表的权利，营造良好的代表履职氛围。', 203, '全年组织226人次参加8期履职能力培训班；做深做实“1+X”立体监督法，镇人大协同各级代表从纵深处、全方位、各维度、细微上着力，让监督更加立体直观，在监督过程中践行全过程人民民主，助力打造“阳光餐饮街区”示范点。', 'DONE', NULL),
	(200, '200', '1、提高全社会对人大监督的认识，为人大监督创造良好的社会环境；2、正确处理各方关系，保证人大监督工作的顺利进行；3、加强人大自身建设，提高人大监督工作的水平。', 205, '切实发挥镇人大监督实效，做深做实“1+X”立体监督法，镇人大协同各级代表从纵深处、全方位、各维度、细微上着力，让监督更加立体直观，在监督过程中践行全过程人民民主，助力打造“阳光餐饮街区”示范点，常态化参与镇域食品安全督查。', 'DONE', NULL),
	(201, '201', '1、强化人大监督，加快推动民生实事项目实施；2、带头做到廉洁从政、廉洁用权，构建亲清政商关系，保持清正廉洁政治本色；3、坚决抵制其他党员干部及其家属亲友违规干预工程建设，推动廉洁人大建设。', 206, '推进人大代表联络监督民生实事项目。切实推动镇十八届人民代表大会票决确定的民生实事项目顺利实施，充分发挥人大代表围绕中心、服务大局、依法监督的重要作用。制定计划、监督推进、项目评价，抓住关键环节、突出效果导向，促进民生实事项目真正成为人民群众满意的民生工程，不断增强人民群众的幸福感、获得感。', 'DONE', NULL),
	(202, '202', '1、加强内部提醒、监督力度，切实防范接受可能影响公正履职的吃请等问题；2、带头垂范，不接受代表、企业、工程单位的宴请、送礼等，树立良好形象。', 207, '行动上，始终严格自我要求，时时提醒自己，自觉管住“三圈”，不拉庸俗关系、不搞团团伙伙。思想上，自觉加强党性修养和锤炼，远离诱惑、约束欲望、收敛行为、知足知乐。', 'DONE', NULL),
