INSERT INTO `fr_meeting_topic_task` (`id`, `content`, `is_matter`, `topic_id`) VALUES
	(1, '加强党的政治建设，增强“四个意识”，坚定“四个自信”，做到“两个维护”，切实提高政治判断力、政治领悟力、政治执行力，坚决贯彻落实习近平总书记重要讲话和重要指示批示精神，坚决贯彻落实党中央决策部署，坚持全面从严治党战略方针，始终保持自我革命定力，推动更好发挥全面从严治党引领保障作用。严肃党内政治生活，加强政治生态研判。带头贯彻执行民主集中制，严格执行党委会议议事规则和集体决策制度，严格落实“五不直接分管”、集体议事决策时末位表态等要求。带头遵守政治纪律和政治规矩，主动向上级请示报告工作。', NULL, 1),
	(2, '认真执行《党委（党组）落实全面从严治党主体责任规定》、《中共中央关于加强对“一把手”和领导班子监督的意见》和省委实施意见，按照深化全面从严治党“四责协同”机制要求，牵头推动落实“五张责任清单”，履行第一责任人责任，加强区（镇）全面从严治党研究部署和督促落实，重点抓好对区（镇）班子成员和村（社）、中心办所“一把手”的监督，抓好职责范围内党风廉政建设和反腐败工作。', NULL, 1),
	(3, '认真贯彻落实省市县委和区（镇）党委决策部署，牵头推动落实招大引强、产业转型、创新赋能、城乡建设、民生保障、社会治理、党建统领“七大提升行动”，深入开展项目攻坚年、改革创新年、作风建设年“三个年”活动。', NULL, 1),
	(4, '加强党对反腐败工作的统一领导，坚持综合施治，牵头落实不敢腐、不能腐、不想腐一体推进，重点推动强化权力公开规范高效运行机制建设，提升基层治理体系和治理能力现代化水平。牵头统筹谋划清廉单元建设，持续深化清廉百步建设。', NULL, 1),
	(5, '牵头加固中央八项规定堤坝，持之以恒加强作风建设，锲而不舍纠“四风”树新风。', NULL, 1),
	(6, '牵头加固中央八项规定堤坝，持之以恒加强作风建设，锲而不舍纠“四风”树新风。', NULL, 1),
	(7, '坚持以身作则，在严格自律上当标杆、作表率，带头遵守执行全面从严治党各项规定，带头落实中央八项规定及其实施细则精神，带头坚决抵制其他党员干部及其家属亲友违规干预干部选拔任用、工程建设、执法司法等行为。带头做到廉洁从政、廉洁用权、廉洁修身、廉洁齐家，带头落实构建亲清政商关系要求，管住管好自己的家属亲友和身边工作人员，始终保持清正廉洁政治本色。', NULL, 1),
	(8, '加强党的政治建设，增强“四个意识”，坚定“四个自信”，做到“两个维护”，切实提高政治判断力、政治领悟力、政治执行力，坚决贯彻落实习近平总书记重要讲话和重要指示批示精神，坚决贯彻落实党中央决策部署，坚持全面从严治党战略方针，始终保持自我革命定力，推动更好发挥全面从严治党引领保障作用。认真贯彻执行民主集中制，严格执行党委会议议事规则和集体决策制度，加强班子成员之间相互监督，讨论决策问题充分发表意见，有效发挥监督作用。严格遵守政治纪律和政治规矩，主动向上级请示报告工作。', NULL, 2),
	(9, '认真执行《党委（党组）落实全面从严治党主体责任规定》、《中共中央关于加强对“一把手”和领导班子监督的意见》和省委实施意见，按照深化全面从严治党“四责协同”机制、落实“五张责任清单”要求，认真履行“一岗双责”，部署、检查、督促落实政府系统全面从严治党工作及责任落实工作，重点抓好对政府系统党员干部，特别是财政办和国资、审计线“一把手”及其成员的严格教育管理监督，抓好政府系统、主管部门（口子）等职责范围内党风廉政建设和反腐败工作，推动廉洁政府建设。', NULL, 2),
	(10, '认真贯彻落实省市县委和区（镇）党委决策部署，推动落实“七大提升行动”，深入开展“三个年”活动。', NULL, 2),
	(11, '强化政府权力公开规范高效运行，谋划、实施职责范围内的重点清廉单元建设，提高治理水平。严格执行财政预算决算制度，加强对财政性资金的管理和监督检查，抓好基础设施、公共建设等政府投资项目的监管，提高资金使用绩效，严格控制和缩减一般性支出，厉行勤俭节约，带头过“紧日子”。切实抓好国有资产管理及处置、土地出让、政府采购、招商引资、债务管理、内控机制数字化改革等方面监督。', NULL, 2),
	(12, '加强政府系统作风建设，推动严格执行中央八项规定及其实施细则精神，持之以恒反“四风”，持续整治享乐主义、奢靡之风，力戒形式主义、官僚主义。', NULL, 2),
	(13, '加强政府系统党员干部日常教育管理监督，运用好“四种形态”特别是第一种形态，让“红红脸、出出汗”成为常态。', NULL, 2),
	(14, '严格廉洁自律，模范遵守执行全面从严治党各项规定，严格落实中央八项规定及其实施细则精神，坚决抵制其他党员干部及其家属亲友违规干预干部选拔任用、工程建设、执法司法等行为。带头做到廉洁从政、廉洁用权、廉洁修身、廉洁齐家，带头落实构建亲清政商关系要求，管住管好自己的家属亲友和身边工作人员，始终保持清正廉洁政治本色。', NULL, 2),
	(15, '加强党的政治建设，增强“四个意识”，坚定“四个自信”，做到“两个维护”，切实提高政治判断力、政治领悟力、政治执行力，坚决贯彻落实习近平总书记重要讲话和重要指示批示精神，坚决贯彻落实党中央决策部署，坚持全面从严治党战略方针，始终保持自我革命定力，推动更好发挥全面从严治党引领保障作用。认真贯彻执行民主集中制，严格执行班子集体决策制度，加强班子成员之间相互监督，讨论决策问题充分发表意见，有效发挥监督作用。严格遵守政治纪律和政治规矩，主动向上级请示报告工作。', NULL, 3),
	(16, '认真执行《党委（党组）落实全面从严治党主体责任规定》、《中共中央关于加强对“一把手”和领导班子监督的意见》和省委实施意见，按照深化全面从严治党“四责协同”机制、落实“五张责任清单”要求，认真履行“一岗双责”，部署、检查、督促落实镇人大全面从严治党工作及责任落实工作，重点抓好对镇人大党员干部，特别是人大办、慈善分会“一把手”及其成员的严格教育管理监督，抓好镇人大、分管部门（口子）等职责范围内党风廉政建设和反腐败工作，推动廉洁人大建设。', NULL, 3),
	(17, '认真贯彻落实省市县委和区（镇）党委决策部署，强化人大监督履职，有力推动落实“七大提升行动”和“三个年”活动，协助抓好民生保障提升行动。', NULL, 3),
	(18, '强化人大监督权力的公开规范高效运行，谋划、实施职责范围内的重点清廉单元建设，提高履职规范化水平。抓好慈善资金募集、分配使用监管，防范化解问题风险。', NULL, 3),
	(19, '创新优化人大监督，推动人大监督与其他各类监督形成协同格局，不断提升监督效能，保障和推动上级决策部署落地见效。', NULL, 3),
	(20, '加强镇人大作风建设，推动严格执行中央八项规定及其实施细则精神，持之以恒反“四风”，持续整治享乐主义、奢靡之风，力戒形式主义、官僚主义。', NULL, 3),
	(21, '加强镇人大、分管部门（口子）党员干部的日常教育管理监督，运用好“四种形态”特别是第一种形态，让“红红脸、出出汗”成为常态。', NULL, 3),
